--
-- Table structure for table `hotels`
--

CREATE TABLE `hotels` (
  `id` int UNSIGNED NOT NULL,
  `location_id` int NOT NULL DEFAULT '0',
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `thumnail` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `slides` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `phone` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `address` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `price` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `type` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `times` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `description` text COLLATE utf8mb4_unicode_ci,
  `maps` text COLLATE utf8mb4_unicode_ci,
  `detail` longtext COLLATE utf8mb4_unicode_ci,
  `status` tinyint DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Dumping data for table `hotels`
--

INSERT INTO `hotels` (`id`, `location_id`, `name`, `slug`, `thumnail`, `slides`, `phone`, `address`, `price`, `type`, `times`, `description`, `maps`, `detail`, `status`, `created_at`, `updated_at`) VALUES
(1, 1, 'Khách sạn cồn phụng--delete--1702900467', 'khach-san-cp--delete--1702900467', 'http://127.0.0.1:8000/uploads/2023/12/cr2-1.jpg', 'http://127.0.0.1:8000/uploads/2023/12/tv01-1.jpg,http://127.0.0.1:8000/uploads/2023/12/tv02-1.jpg', '0932341051', 'Phường Lê Bình, Quận Cái Răng, Thành phố Cần Thơ', '~300k', 'Khách sạn', '6:00 : 12:00', 'mô tả ngắn', NULL, '<p>mô tả chi chiết</p>', 4, '2023-12-17 13:27:26', '2023-12-17 13:27:26'),
(2, 1, 'Khách Sạn Mêkong Bến Tre', 'ks-cp', 'http://127.0.0.1:8000/uploads/2023/12/cp3.jpg', 'http://127.0.0.1:8000/uploads/2023/12/cp2.jpg,http://127.0.0.1:8000/uploads/2023/12/cp3-1.jpg', '098729 9718', 'Hamlet 9, Phuoc Long Commune, Giong Trom District Ben Tr', '~300k', 'khách sạn', 'Bắt đầu nhận phòng : 13:00, kết thúc nhận phòng 14:00 hôm sau', 'Nằm ở tỉnh Bến Tre, Mekong Hotel & Restaurant có khu vườn và sân hiên. Trong số các tiện nghi tại chỗ nghỉ này còn có lễ tân 24 giờ, phòng giữ hành lý và WiFi miễn phí trong toàn bộ khuôn viên. Khách sạn cung cấp chỗ đỗ xe riêng miễn phí và dịch vụ cho thuê xe hơi cho những khách muốn khám phá khu vực xung quanh.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15704.65789932367!2d106.34410294581104!3d10.248315487920513!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310aa893b8100001%3A0x9be84990bd473c17!2sMekong%20Hotel!5e0!3m2!1svi!2s!4v1702903283636!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Tìm kiếm khách sạn lý tưởng ở Bến Tre không phải là quá khó. Chào đón bạn đến với Mekong Hotel &amp; Restaurant, một lựa chọn đúng đắn cho những du khách như bạn.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Mekong Hotel &amp; Restaurant cung cấp bàn lễ tân 24 giờ, dịch vụ phòng và đồ đạc ngoài trời, làm cho chuyến đi của bạn dễ chịu hơn. Nếu bạn lái xe tới Mekong Hotel &amp; Restaurant, đỗ xe miễn phí có sẵn.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Khi ở Bến Tre hãy nhớ thử nhà hàng kiểu việt lân cận như Cafe Làng Văn 2, Vườn Ẩm Thực Út An hoặc Lucid Dream - Tea &amp; Coffee.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Nếu bạn tìm kiếm các hoạt động giải trí, bạn có thể thử Ben Tre Museum, vốn là viện bảo tàng lịch sử yêu thích với khách du lịch.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Mekong Hotel &amp; Restaurant sẽ giúp bạn khám phá Bến Tre một cách nhanh chóng và dễ dàng nhất. Kì nghỉ của bạn sẽ trở nên thư giãn và dễ chịu</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"> </span></p>', 1, '2023-12-17 21:54:51', '2023-12-21 03:15:51'),
(3, 3, 'Khách sạn An Khang', 'khach-san-ankhang', 'http://127.0.0.1:8000/uploads/2023/12/ak-ht3.jpg', 'http://127.0.0.1:8000/uploads/2023/12/ak-ht.jpg,http://127.0.0.1:8000/uploads/2023/12/ak-ht2.jpg', '0972676499', '69, Võ Văn Kiệt, Phường 7 , thành phố Trà Vinn, Tỉnh Trà Vinh', '~250k/ 1 phòng đôi', 'Khách sạn', 'Bắt đầu nhận phòng : 13:00, kết thúc nhận phòng 14:00 hôm sau', 'Tọa lạc tại thành phố Trà Vinh, AN KHANG HOTEL cung cấp chỗ nghỉ với sân hiên riêng. Trong số các tiện nghi của chỗ nghỉ này có lễ tân 24 giờ, dịch vụ phòng và WiFi miễn phí trong toàn bộ khuôn viên. Chỗ nghỉ có thể bố trí chỗ đỗ xe riêng cho khách với một khoản phụ phí', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15719.801213844477!2d106.31832794867587!3d9.938093142625151!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a017fa5c366b8f%3A0x23d8c40cc869f692!2sHotel%20An%20Khang!5e0!3m2!1svi!2s!4v1702951624693!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Trà Vinh, một thành phố nằm tại vùng Đồng Bằng Sông Cửu Long, Việt Nam, là điểm đến thu hút du khách bởi không chỉ vẻ đẹp tự nhiên mà còn bởi sự đa dạng của các lựa chọn lưu trú</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Tọa lạc tại thành phố Trà Vinh, AN KHANG HOTEL cung cấp chỗ nghỉ với sân hiên riêng. Trong số các tiện nghi của chỗ nghỉ này có lễ tân 24 giờ, dịch vụ phòng và WiFi miễn phí trong toàn bộ khuôn viên. Chỗ nghỉ có thể bố trí chỗ đỗ xe riêng cho khách với một khoản phụ phí.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Phòng nghỉ tại khách sạn được trang bị máy điều hòa, truyền hình cáp màn hình phẳng, tủ lạnh, ấm đun nước, chậu rửa vệ sinh (bidet), đồ vệ sinh cá nhân miễn phí và tủ để quần áo. Tất cả các phòng còn có phòng tắm riêng với vòi sen.Khách lưu trú tại AN KHANG HOTEL có thể thưởng thức bữa sáng tự chọn.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Sân bay gần nhất là sân bay quốc tế Cần Thơ, cách 88 km từ AN KHANG HOTEL.Hãy tận hưởng chuyến ghé thăm Trà Vinh với kỳ lưu trú tại Khách sạn An Khang. Khách sẽ được hưởng các tiện nghi, dịch vụ miễn phí như Wifi, bãi đậu xe có người phục vụ và bãi đậu xe tự phục vụ.</span></p>', 1, '2023-12-18 12:07:15', '2023-12-21 03:16:01'),
(4, 1, 'Khu Nghỉ Dưỡng Forever Green', 'khu-nghi-duong-FG', 'http://127.0.0.1:8000/uploads/2023/12/ff1.jpg', 'http://127.0.0.1:8000/uploads/2023/12/ff3.jpg,http://127.0.0.1:8000/uploads/2023/12/ff4.jpg,http://127.0.0.1:8000/uploads/2023/12/ff2.jpg', '02753618619', 'Phú Khương Phú Túc, Bến Tre', '~300k', 'Khách sạn', 'Nhận phòng vào lúc 12:00 và trả phòng vào lúc 12:00 hôm sau', 'Cách TP.HCM khoảng 80 km, du khách chỉ mất hơn 60 phút theo đường cao tốc, vượt qua cầu dây văng Rạch Miễu nối liền hai bờ sông Tiền là đã đặt chân đến xứ dừa Bến Tre. Từ đây, du khách đi khoảng 10 km nữa sẽ bắt gặp một “hòn ngọc xanh” nằm trọn trong lòng vùng đất trù phú mà thiên nhiên đã ban tặng  – Forever Green Resort', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15701.84088249635!2d106.24705444875465!3d10.305004737062928!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310aa475a913eb99%3A0x12e3702bf6150d6a!2sForever%20Green%20Resort!5e0!3m2!1svi!2s!4v1702952289116!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Cách TP.HCM khoảng 80 km, du khách chỉ mất hơn 60 phút theo đường cao tốc, vượt qua cầu dây văng Rạch Miễu nối liền hai bờ sông Tiền là đã đặt chân đến xứ dừa Bến Tre. Từ đây, du khách đi khoảng 10 km nữa sẽ bắt gặp một “hòn ngọc xanh” nằm trọn trong lòng vùng đất trù phú mà thiên nhiên đã ban tặng  – Forever Green Resort. Đây là một khu nghỉ dưỡng cao cấp được hình thành nên từ chính những nét đặc trưng sẵn có nơi đây. Ngay từ cái tên đã nói lên được ý nghĩa và mục tiêu mà khu resort này mong muốn mang lại cho du khách – đó là “về với thiên nhiên”.</span><br /><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\"> </span><br /><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Với tổng diện tích 21 hecta và được chia thành 3 khu chính, ngoài việc cung cấp đầy đủ các chức năng và dịch vụ theo tiêu chuẩn 4 sao như khách sạn villa sân vườn, nhà hàng, spa, phòng hội nghị, hồ bơi, karaoke, hát với nhau, xem đàn ca tài tử… Với lợi thế không gian rộng rãi và xanh mát, Forever Green Resort còn tạo nên sự khác biệt và độc đáo cho riêng mình bằng các dịch vụ giải trí đa dạng như câu cá, chèo thuyền kayak, hái trái cây, trò chơi dân gian, đốt lửa trại, khám phá làng quê, đi thuyền xem đom đóm trong đêm…</span><br /><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\"> </span><br /><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Với phương châm hoạt động “Tất cả vì sự hài lòng của du khách”, chúng tôi luôn trân trọng từng phút giây nghỉ dưỡng của du khách, tạo cho du khách những ấn tượng không thể nào quên. Hãy đến với chúng tôi để cảm nhận thế nào là thế giới yên bình của thiên nhiên!</span></p>', 1, '2023-12-18 12:18:38', '2023-12-21 03:16:10'),
(5, 5, 'Azerai Cần Thơ resort--delete--1703080450', 'can-tho-resort--delete--1703080450', 'http://127.0.0.1:8000/uploads/2023/12/can-tho-2-1.jpg', 'http://127.0.0.1:8000/uploads/2023/12/can-tho-1.jpg,http://127.0.0.1:8000/uploads/2023/12/can-tho-2-2.jpg,http://127.0.0.1:8000/uploads/2023/12/can-tho-3-1.jpg', '0292 3627 888', 'Cồn Ấu, Phường Hưng Phú, Quận Cái Răng, Thành phố Cần Thơ', '~2.500.000 một căn/ 1 đêm', 'Resort', 'Theo ngày', 'Nằm nơi cồn Ấu hoang sơ và trong lành, bốn bề là không gian yên ả của dòng sông Hậu hiền hòa, khu nghỉ dưỡng Azerai Cần Thơ trải rộng trên diện tích hơn 8 héc-ta với hệ thống phòng hiện đại và đẳng cấp, được thiết kế theo lối kiến trúc truyền thống pha chút hơi thở hiện đại. Azerai Cần Thơ có diện tích hơn tám ha với 60 phòng. Thiết kế được lấy cảm hứng từ kiến ​​trúc truyền thống, đạt được sự giải thích đương đại.  Nội thất có tông màu nhẹ và trung tính. Mỗi phòng rộng khoảng 35m2, có tầm nhìn ra sông, hồ hay những khu vườn và cây cối xung quanh. Mỗi phòng đều được bao quanh bởi những khu vườn nhiệt đới tươi tốt Tất cả các phòng đều có khu vực phòng tắm rộng rãi, giường kết hợp và khu vực tiếp khách Ngoài sân hiên ngoài trời với ghế dài...', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15715.033630302605!2d105.79249274869676!3d10.036784241126457!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a062b92e4996cf%3A0x3533d5f2d9d67037!2zQXplcmFpIEPhuqduIFRoxqE!5e0!3m2!1svi!2s!4v1702952638110!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<div id=\"place_info\" class=\"modal fade pu-ut show\" tabindex=\"-1\" role=\"dialog\" data-bs-backdrop=\"static\" data-bs-keyboard=\"false\" aria-labelledby=\"staticBackdropLabel\" aria-modal=\"true\">\r\n<div class=\"modal-dialog\">\r\n<div class=\"modal-content pop-up-fix\">\r\n<div class=\"modal-body\">\r\n<p style=\"text-align: justify;\">Nằm nơi cồn Ấu hoang sơ và trong lành, bốn bề là không gian yên ả của dòng sông Hậu hiền hòa, khu nghỉ dưỡng Azerai Cần Thơ trải rộng trên diện tích hơn 8 héc-ta với hệ thống phòng hiện đại và đẳng cấp, được thiết kế theo lối kiến trúc truyền thống pha chút hơi thở hiện đại. Azerai Cần Thơ có diện tích hơn tám ha với 60 phòng. Thiết kế được lấy cảm hứng từ kiến ​​trúc truyền thống, đạt được sự giải thích đương đại. </p>\r\n<p style=\"text-align: justify;\">Nội thất có tông màu nhẹ và trung tính. Mỗi phòng rộng khoảng 35m2, có tầm nhìn ra sông, hồ hay những khu vườn và cây cối xung quanh. Mỗi phòng đều được bao quanh bởi những khu vườn nhiệt đới tươi tốt Tất cả các phòng đều có khu vực phòng tắm rộng rãi, giường kết hợp và khu vực tiếp khách Ngoài sân hiên ngoài trời với ghế dài. Các tiện nghi bao gồm TV, mini bar, máy pha trà / cà phê cá nhân. Lake Room áp dụng việc sử dụng các vật liệu địa phương cho các tiện nghi hiện đại ngày nay. Nội thất có tông màu nhẹ và trung tính. Mỗi phòng rộng khoảng 35m2, có tầm nhìn ra hồ sen. River Room áp dụng việc sử dụng các vật liệu địa phương cho các tiện nghi hiện đại ngày nay. Nội thất có tông màu nhẹ và trung tính. Mỗi phòng rộng khoảng 35m2, nhìn ra toàn cảnh sông Hậu. Để tận hưởng sự riêng tư và tách biệt, Mekong Villa là một ốc đảo yên bình cho những người thân yêu, bạn bè thân thiết và các đối tác kinh doanh của bạn. Biệt thự tự hào có một hồ bơi riêng rộng 6x20m và tính linh hoạt tối đa với nhiều cấu hình phòng khác nhau có thể, được thiết kế riêng theo yêu cầu của bạn.</p>\r\n<p style=\"text-align: justify;\"> </p>\r\n<p style=\"text-align: justify;\">Đắm mình trong hồ bơi vô tận tại thiên đường nghỉ dưỡng Azerai Cần Thơ, quý khách sẽ hòa mình vào làn nước trong xanh đến tuyệt hảo. Ngoài ra, để mang đến cho quý khách những trải nghiệm thư giãn tuyệt vời nhất, hồ bơi còn được bố trí những chiếc ghế tắm nắng thoải mái và đi-văng sang trọng.</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<section class=\"hotel-information-detail bgc-green-super\">\r\n<div class=\"container\"> </div>\r\n</section>', 4, '2023-12-18 12:24:05', '2023-12-19 23:53:42'),
(6, 5, 'Homestay Cái Sơn', 'homestay-cai-son', 'http://127.0.0.1:8000/uploads/2023/12/homestay-ct.jpg', 'http://127.0.0.1:8000/uploads/2023/12/homestay-ct1-1.jpg,http://127.0.0.1:8000/uploads/2023/12/homestay-ct2-1.jpg', '0986266401', '250/6 Khu vực 6, Phường An Bình, Quận Ninh Kiều, Thành phố Cần Thơ', '1.000.000/căn', 'Homestay', 'Theo ngày', 'Tọa lạc cạnh một con sông, Homestay Cái Sơn cung cấp nơi lánh mình thư giãn ở thành phố Cần Thơ. Nằm nép mình trong một khu vườn, chỗ nghỉ nhà dân này cung cấp các phòng thoải mái kèm tiện nghi phòng tắm riêng và Wi-Fi miễn phí. Chỗ nghỉ này nằm cách Trung tâm Thành phố Cần Thơ 12 km và cách Sân bay Nội địa Cần Thơ 18 km. Chợ nổi Cái Răng nằm cách đó 6 km trong khi Bến xe Cần Thơ cách nơi ở này 8 km. Các phòng tại Cái Sơn Homestay Can Tho được trang bị giường giản dị và màn chống muỗi. Khách có thể ngắm nhìn khu vườn từ tất cả các phòng...', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d251459.27301082973!2d105.58686259587377!3d10.012634613859186!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a0886db76c5e85%3A0x451e86a4ca5ca29f!2sHomestay%20ut%20hoai%20cai%20son!5e0!3m2!1svi!2s!4v1702953311154!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Tọa lạc cạnh một con sông,Cái Sơn Homestay cung cấp nơi lánh mình thư giãn ở thành phố Cần Thơ. Nằm nép mình trong một khu vườn, chỗ nghỉ nhà dân này cung cấp các phòng thoải mái kèm tiện nghi phòng tắm riêng và Wi-Fi miễn phí.</span><br /><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Chỗ nghỉ này nằm cách Trung tâm Thành phố Cần Thơ 12 km và cách Sân bay Nội địa Cần Thơ 18 km. Chợ nổi Cái Răng nằm cách đó 6 km trong khi Bến xe Cần Thơ cách nơi ở này 8 km.</span><br /><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Các phòng tại Cái Sơn Homestay Can Tho được trang bị giường giản dị và màn chống muỗi. Khách có thể ngắm nhìn khu vườn từ tất cả các phòng.</span><br /><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Du khách có thể tận dụng dịch vụ cho thuê xe đạp miễn phí. Nhà khách còn có quầy lễ tân 24 giờ và cung cấp dịch vụ đưa đón sân bay với một khoản phụ phí.</span><br /><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Nhà hàng phục vụ ăn uống cả ngày với các món ngon của Việt Nam. Nơi nghỉ này còn cung cấp dịch vụ giặt là. Dịch vụ đưa đón đến sân bay cũng được bố trí kèm phụ phí.Các cặp đôi đặc biệt thích địa điểm này — họ cho điểm 8,3 cho kỳ nghỉ dành cho 2 người.</span><br /><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Homestay này cũng được đánh giá là đáng giá tiền nhất ở Cần Thơ! Khách sẽ tiết kiệm được nhiều hơn so với nghỉ tại những chỗ nghỉ khác ở thành phố này.</span></p>', 1, '2023-12-18 12:35:21', '2023-12-21 03:16:20'),
(7, 6, 'Khách sạn Victoria Cần Thơ', 'khach-san-victoria', 'http://127.0.0.1:8000/uploads/2023/12/victory1-2.jpg', 'http://127.0.0.1:8000/uploads/2023/12/victory2-1.jpg,http://127.0.0.1:8000/uploads/2023/12/victory3-1.jpg,http://127.0.0.1:8000/uploads/2023/12/victory4-1.jpg,http://127.0.0.1:8000/uploads/2023/12/victory5-1.jpg', '3810111', 'Khu Bãi Cát, Phường Cái Khế, Quận Ninh Kiều, Thành phố Cần Thơ', '5.600.000 - 7.900.000 đồng', 'Khách sạn', 'Bắt đầu nhận phòng : 13:00, kết thúc nhận phòng 14:00 hôm sau', 'Nằm bên bờ Sông Hậu, Victoria Can Tho Resort cung cấp chỗ nghỉ đạt chuẩn 4 sao tại một góc yên tĩnh của thành phố Cần Thơ. Với kiến ​​trúc kiểu thuộc địa, resort nằm giữa các khu vườn với những loại cây ngoại lai, nơi du khách có thể thư giãn và nhâm nhi đồ uống trong lúc tắm nắng bên hồ bơi ngoài trời. Các phòng lắp máy điều hòa tại đây được trang trí với sắc be nhạt của vải và nâu của đồ nội thất gỗ sẫm màu. Trong phòng trang bị khu vực ghế ngồi, truyền hình vệ tinh màn hình phẳng và tủ lạnh mini. Một số phòng có ban công riêng. Phòng tắm riêng đi kèm bồn tắm, vòi sen và đồ vệ sinh cá nhân miễn phí. Victoria Can Tho Resort có 7 phòng trị liệu với nhiều liệu pháp mát-xa, bao gồm từ mát-xa kiểu Việt Nam truyền thống cho tới mát-xa chữa bệnh toàn thân. Resort có quầy lễ tân 24 giờ và bàn đặt tour để hỗ trợ khách với nhiều dịch vụ, bao gồm dịch vụ nhận/trả phòng cấp tốc, giữ hành lý và đưa đón sân bay. Victoria Can Tho ...', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1625.5247201990749!2d105.79348970190908!3d10.041214824905357!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a062a4ce2fae3d%3A0xd3def17147ca46d5!2zS2jDoWNoIHPhuqFuIFZpY3RvcmlhIEPhuqduIFRoxqEsIFPDtG5nIEjhuq11LCBDw6FpIEto4bq_LCBOaW5oIEtp4buBdSwgQ-G6p24gVGjGoSwgVmnhu4d0IE5hbQ!5e0!3m2!1svi!2s!4v1703170055415!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Nằm bên bờ Sông Hậu, Victoria Can Tho Resort cung cấp chỗ nghỉ đạt chuẩn 4 sao tại một góc yên tĩnh của thành phố Cần Thơ. Với kiến ​​trúc kiểu thuộc địa, resort nằm giữa các khu vườn với những loại cây ngoại lai, nơi du khách có thể thư giãn và nhâm nhi đồ uống trong lúc tắm nắng bên hồ bơi ngoài trời.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Các phòng lắp máy điều hòa tại đây được trang trí với sắc be nhạt của vải và nâu của đồ nội thất gỗ sẫm màu. Trong phòng trang bị khu vực ghế ngồi, truyền hình vệ tinh màn hình phẳng và tủ lạnh mini. Một số phòng có ban công riêng. Phòng tắm riêng đi kèm bồn tắm, vòi sen và đồ vệ sinh cá nhân miễn phí.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Victoria Can Tho Resort có 7 phòng trị liệu với nhiều liệu pháp mát-xa, bao gồm từ mát-xa kiểu Việt Nam truyền thống cho tới mát-xa chữa bệnh toàn thân. Resort có quầy lễ tân 24 giờ và bàn đặt tour để hỗ trợ khách với nhiều dịch vụ, bao gồm dịch vụ nhận/trả phòng cấp tốc, giữ hành lý và đưa đón sân bay.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Victoria Can Tho Resort cách Cầu đi bộ Bến Ninh Kiều 700 m và Sân vận động Cần Thơ cũng như Bảo tàng Cần Thơ 1 km. Sân bay gần nhất là sân bay quốc tế Cần Thơ, cách đó 12 km.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Nhà hàng Spices trong khuôn viên nhìn ra Sông Hậu và có khu vực ghế ngồi trong nhà lẫn ngoài trời. Tại đây phục vụ ẩm thực Âu và Việt, được chuẩn bị bởi đầu bếp người Pháp của khách sạn.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Chỗ nghỉ này là một trong những vị trí được đánh giá tốt nhất ở Cần Thơ! Khách thích nơi đây hơn so với những chỗ nghỉ khác trong khu vực.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Các cặp đôi đặc biệt thích địa điểm này — họ cho điểm <strong>9,7</strong> cho kỳ nghỉ dành cho 2 người.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Resort này cũng được đánh giá là đáng giá tiền nhất ở Cần Thơ! Khách sẽ tiết kiệm được nhiều hơn so với nghỉ tại những chỗ nghỉ khác ở thành phố này.</span></p>', 1, '2023-12-21 00:47:41', '2023-12-21 03:16:31'),
(8, 7, 'Khách sạn Senior Cần Thơ', 'senior-can-tho', 'http://127.0.0.1:8000/uploads/2023/12/senior-can-tho.jpg', 'http://127.0.0.1:8000/uploads/2023/12/senior-can-tho1.jpg,http://127.0.0.1:8000/uploads/2023/12/senior-can-tho2.jpg,http://127.0.0.1:8000/uploads/2023/12/senior-can-tho3.jpg', '02923818339', '46 Đường Trần Đại Nghĩa, Phường Cái Khế, Quận Ninh Kiều, Thành phố Cần Thơ', '350.000đ - 590.000đ', 'Khách sạn', 'Bắt đầu nhận phòng : 13:00, kết thúc nhận phòng 14:00 hôm sau', 'KHÁCH SẠN SENIOR CẦN THƠ – một địa điểm lưu trú lý tưởng – sở hữu một kiến trúc mang phong cách Châu Âu 06 tầng với 30 phòng nghỉ tiện nghi, thoáng mát. Bất cứ khi nào đến với Senior bạn luôn được hân hoan chào đón bởi sự chuyên nghiệp – ân cần – thân thiện – mến khách của tập thể nhân viên khách sạn. Tọa lạc ngay Trung Tâm Thương Mại Cần Thơ sầm uất. Thuận tiện cho những chuyến công tác, tham quan và chỉ mất 05 phút để đi đến Trung Tâm Hội Chợ Triễn Lãm Quốc Tế EFC, Sân Vận Động Cần Thơ hoặc những điểm tham quan nổi tiếng của Thành Phố như Bến Ninh Kiều, Cầu Đi Bộ, Công Viên Sông Hậu, Chợ Đêm Cần Thơ…', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15715.347879103338!2d105.77704134869536!3d10.030308741224728!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a0632cc8cabe2f%3A0xb7e3a012f5bba9ed!2zS2jDoWNoIFPhuqFuIFNlbmlvciAyIEPhuqduIFRoxqE!5e0!3m2!1svi!2s!4v1703171013206!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Cùng những nhà hàng nổi tiếng tại Cần Thơ với các món đặc sản và nhìn ngắm Sông Hậu hay những khu ăn uống nhộn nhịp về đêm… Chúng tôi hiểu rằng việc chăm sóc từng giấc ngủ, mang đến sự thoải mái, hài lòng cho Quý Khách là sứ mệnh và chính những điều này làm nên sự thành công cho mọi hoạt động của ...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"><strong>KHÁCH SẠN SENIOR CẦN THƠ</strong> – một địa điểm lưu trú lý tưởng – sở hữu một kiến trúc mang phong cách Châu Âu 06 tầng với 30 phòng nghỉ tiện nghi, thoáng mát. Bất cứ khi nào đến với Senior bạn luôn được hân hoan chào đón bởi sự chuyên nghiệp – ân cần – thân thiện – mến khách của tập thể nhân viên khách sạn. Tọa lạc ngay Trung Tâm Thương Mại Cần Thơ sầm uất. Thuận tiện cho những chuyến công tác, tham quan và chỉ mất 05 phút để đi đến Trung Tâm Hội Chợ Triễn Lãm Quốc Tế EFC, Sân Vận Động Cần Thơ hoặc những điểm tham quan nổi tiếng của Thành Phố như Bến Ninh Kiều, Cầu Đi Bộ, Công Viên Sông Hậu, Chợ Đêm Cần Thơ… Cùng những nhà hàng nổi tiếng tại Cần Thơ với các món đặc sản và nhìn ngắm Sông Hậu hay những khu ăn uống nhộn nhịp về đêm… Chúng tôi hiểu rằng việc chăm sóc từng giấc ngủ, mang đến sự thoải mái, hài lòng cho Quý Khách là sứ mệnh và chính những điều này làm nên sự thành công cho mọi hoạt động của chúng tôi với tâm niệm không ngừng vươn đến sự hoàn hảo.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"><strong>Các điểm thu hút:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"><strong>+ Bến Ninh Kiều:</strong> Đây là khu vực mà bạn có thể bách bộ theo dọc bờ song, tìm thấy rất nhiều quán Bar, cà phê, của hàng lưu niệm, nhà hàng theo phong cách Phương Tây, Á Đông, Món Ý… với các món ăn nổi tiếng cùng các đầu bếp đến từ các Quốc gia này.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"><strong>+ Chợ – Siêu thị – Trung tâm Thương mại:</strong> hệ thống các siêu thị lớn nhất Việt Nam như Vincom, Coopmart; Trung tâm thương mại Cần Thơ, Chợ Cổ Tây đô… với rất nhiều các loại đặc sản địa phương, đa dạng các loại quần áo, thời trang cao cấp; chuổi thức ăn nhanh…</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"><strong>+ Du thuyền:</strong> Đưa du khách dạo trên Sông Hậu, ngắm nhìn cầu Cần Thơ hoành tráng – hiện đại nhất Đông Nam Á – Du khách có thể thưởng thức các món ăn dân dã miền tây; thưởng thức âm nhạc truyền thống , đờn ca tài tử, dân ca hoặc du khách có thể đăng ký hát tặng bạn bè người thân và gia đình cùng với nhiều hoạt động hằng ngày, hằng tuần khác.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"><strong>+ Dịch vụ thuê tàu – đò – xe:</strong> phục vụ du khách khám phá các khu làng nghề ở các vùng ven sông Hậu và Ninh Kiều ; song song với những địa danh nổi tiếng khác như Chợ nổi Cái Răng, Chợ nổi Phong Điền, Vườn trái cây Mỹ Khánh. Bên cạnh đó sẽ thật thiếu xót dành cho du khách thích loại hình du lịch tâm linh khi một lần được trải nghiệm địa điểm mới THIỀN VIỆN TRÚC LÂM PHƯƠNG NAM và ĐÌNH CỔ BÌNH THỦY.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"><strong>+ Coffee Shop:</strong> SENIOR CAFÉ nằm trên trục đường chính dẫn đến Bến Ninh Kiều là địa điểm sang trọng nổi bậc trong chuổi những quán café tại Bến Ninh Kiều dành cho Quý khách hàng sành điệu với kiến trúc sang trọng, hiện đại, và tiện nghi.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"><strong>+ Dịch vụ – Vui chơi – Giải trí:</strong> Ngay trong khu vực Trung tâm thương mại là các khu liền kề: Khu Thể thao đa năng cần Thơ, Trung tâm hội chợ Quốc Tế Cần Thơ, các trung tâm hội nghị , tiệc cưới đã đang và sẽ hoạt động thuộc loại sang trọng tiện nghi bậc nhất tại ĐBSCL.</span></p>', 1, '2023-12-21 01:03:40', '2023-12-21 03:16:43'),
(9, 8, 'Flower & Frog Homestay Sa Đéc', 'F-F-homestay', 'http://127.0.0.1:8000/uploads/2023/12/f-f.jpg', 'http://127.0.0.1:8000/uploads/2023/12/f-f-1.jpg,http://127.0.0.1:8000/uploads/2023/12/f-f1.jpg,http://127.0.0.1:8000/uploads/2023/12/f-f2.jpg,http://127.0.0.1:8000/uploads/2023/12/f-f3.jpg', '0918645390', 'Làng hoa Tân Quy Dong, Sa Dec, Đồng Tháp, Sa Đéc', '400.000 - 1.000.000đ', 'Homestay', 'Bắt đầu nhận phòng : 13:00, kết thúc nhận phòng 14:00 hôm sau', 'Flower & Frog Homestay ở Sa Ðéc cung cấp chỗ nghỉ với tầm nhìn ra vườn, khu vườn, quầy bar và Wi-Fi miễn phí. Nơi đây có sân hiên và/hoặc sân trong với tầm nhìn ra sông, du khách có thể thuê xe đạp và cách thành phố Vĩnh Long 30 km, sân bay gần nhất là sân bay Rạch Giá 95 km.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15701.373232083806!2d105.74801244875674!3d10.314385836921062!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310a7e75340c82bb%3A0x6763502ef0c967b3!2zTmjDoCBow6BuZyBob2Eg4bq_Y2g!5e0!3m2!1svi!2s!4v1703171652389!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Flower &amp; Frog Homestay ở Sa Ðéc cung cấp chỗ nghỉ với tầm nhìn ra vườn, khu vườn, quầy bar và Wi-Fi miễn phí. Nơi đây có sân hiên và/hoặc sân trong với tầm nhìn ra sông, du khách có thể thuê xe đạp và cách thành phố Vĩnh Long 30 km, sân bay gần nhất là sân bay Rạch Giá 95 km.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Homestay Hoa Ếch, tọa lạc tại làng hoa xinh đẹp Sa Đéc, tỉnh Đồng Tháp, là một sự lựa chọn ấm cúng và yên bình. Chủ nhân và nhân viên thân thiện và chu đáo, đảm bảo một kỳ nghỉ dễ chịu cho khách. Các phòng ốc sạch sẽ và thoải mái, tạo nên một môi trường tươi mới. Homestay nằm gần sông Tiền Giang, mang đến khung cảnh đẹp và bình yên. Nhà hàng tại đây phục vụ các món ngon địa phương, bao gồm \"Gỏi tép bông điên điển\" và \"Ếch núp lùm\" được đề xuất nhiệt tình. Khách cũng có thể tận hưởng một cuộc dạo chơi thoải mái dọc bờ sông. Tổng thể, Homestay Hoa Ếch là sự lựa chọn tuyệt vời cho những ai muốn trải nghiệm một kỳ nghỉ thư giãn và thú vị tại trung tâm của Đồng Bằng Sông Cửu Long.</span></p>', 1, '2023-12-21 01:14:19', '2023-12-21 03:16:55'),
(10, 9, 'Khách sạn New Palace', 'khach-san-new-place', 'http://127.0.0.1:8000/uploads/2023/12/new-palace.jpg', 'http://127.0.0.1:8000/uploads/2023/12/new-palace2.jpg,http://127.0.0.1:8000/uploads/2023/12/new-palace3.jpg,http://127.0.0.1:8000/uploads/2023/12/new-palace1.jpg', '02913949888', 'Nằm ở đường Trần Quang Diệu, phường 1 ngay trong trung tâm Bạc Liêu', '735.000-2.000.000 VNĐ', 'Khách sạn', 'Bắt đầu nhận phòng : 13:00, kết thúc nhận phòng 14:00 hôm sau', 'Khách sạn New Palace Bạc Liêu nằm ở đường Trần Quang Diệu, phường 1 ngay trong trung tâm Bạc Liêu. Khách sạn sở hữu vị trí đắc địa đường xá thuận tiện đi lại. Đặc biệt New Palace còn rất gần các khu vực du lịch nổi tiếng của Bạc Liêu. Chính vì thế, khách sạn Bạc Liêu này thu hút được rất nhiều du khách và khách công tác ghé đến nghỉ dưỡng.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15749.217425759509!2d105.72115134854675!3d9.306276952263165!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a1090bd7859c31%3A0x7c8f8d8b8d39807e!2zS2jDoWNoIHPhuqFuIE5ldyBQYWxhY2U!5e0!3m2!1svi!2s!4v1703172092375!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Khách sạn được thiết kế sang trọng. Không gian được xây dựng lấy cảm hứng từ kiến trúc châu Âu thời thượng. Bên ngoài khách sạn được sơn màu trắng tạo nên điểm nhấn cực kỳ ấn tượng trong nền trời xanh.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Khách sạn sở hữu hệ thống phòng nghỉ đa dạng. Tổng số phòng của khách sạn là 129 căn. Mỗi căn sẽ được trang bị đầy đủ thiết bị tiện nghi và có không gian thoáng mát, sạch sẽ. Đặc biệt, khách sạn sử dụng rất nhiều đồ nội thất gỗ nâu trầm dù ở bất kì hạng phòng nào. Đây chính là điểm độc đáo gây ấn tượng lớn tới du khách.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Điểm cộng sáng giá của khách sạn New Palace chính là đa dạng dịch vụ tiện ích rất chuyên nghiệp và đẳng cấp. Du khách đặt phòng của khách sạn sẽ được trải nghiệm nhiều dịch vụ miễn phí. Tất cả dịch vụ của New Palace Bạc Liêu đều mang đến sự thoải mái và thuận tiện cho du khách.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Khách sạn New Palace còn có nhiều điểm nhấn độc đáo về các loại dịch vụ. Đây là một trong số ít khách sạn đồng ý cho thú cưng được phép theo chủ vào cùng. Nhân viên của khách sạn lịch sự, nhã nhặn luôn hỗ trợ mọi người nhiệt tình nhất có thể. Đảm bảo du khách sẽ nhiều trải nghiệm tuyệt vời ở New Palace Bạc Liêu.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">hách sạn New Palace sở hữu diện tích khuôn viên và hệ thống phòng siêu lớn. Các hạng phòng ở đây đều rộng rãi đầy đủ tiện nghi. Hơn nữa, phòng nào cũng được dọn dẹp sạch sẽ cẩn thận. Mỗi hạng phòng sẽ có giá thành phù hợp đáp ứng nhu cầu nghỉ ngơi của du khách. Phòng New Palace không nghỉ có phòng dành cho hai người, mà còn phòng dành cho nhóm bạn hay gia đình.</span></p>', 1, '2023-12-21 01:22:06', '2023-12-21 03:17:03'),
(11, 10, 'SUNRISE Hotel Bạc Liêu', 'sunrise-bac-lieu', 'http://127.0.0.1:8000/uploads/2023/12/sunrise1.jpg', 'http://127.0.0.1:8000/uploads/2023/12/sunrise1-1.jpg,http://127.0.0.1:8000/uploads/2023/12/sunrise2.jpg,http://127.0.0.1:8000/uploads/2023/12/sunrise3.jpg', '0943471236', 'Khu Villa Vincom, HG 37-39, Đường Trần Huỳnh, Phường 7, TP. Bạc Liêu, Tỉnh Bạc Liêu', 'từ 640.000₫', 'Khách sạn', 'Bắt đầu nhận phòng : 13:00, kết thúc nhận phòng 14:00 hôm sau', 'Được đặt tại Bạc Liêu, cách 7,5 km từ Khu dự trữ chim Bạc Liêu, SUNRISE Hotel Bạc Liêu có các phòng nghỉ với phòng chờ chung, bãi đỗ xe riêng miễn phí và quán bar. Với Wi-Fi miễn phí, khách sạn 3 sao này cung cấp dịch vụ phòng và lễ tân 24 giờ. Một số phòng tại đây có ban công với tầm nhìn ra thành phố. Tại khách sạn, các phòng nghỉ được trang bị tủ quần áo. Đầy đủ với phòng tắm riêng có vòi sen và đồ vệ sinh cá nhân miễn phí, các phòng tại SUNRISE Hotel Bạc Liêu có trang bị TV màn hình phẳng và điều hòa không khí, và một số phòng có sân thượng. Tại chỗ ở, các phòng được trang bị chăn và khăn tắm. Khách tại SUNRISE Hotel Bạc Liêu sẽ có cơ hội thưởng thức các hoạt động trong và xung quanh Bạc Liêu, như leo núi. Sân bay gần nhất là Sân bay Cà Mau, cách khách sạn 62 km.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15749.926477862386!2d105.70814204854364!3d9.290522252504426!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a1091019628527%3A0x8ca1339c582d7985!2zU3VucmlzZSBIb3RlbCBC4bqhYyBMacOqdQ!5e0!3m2!1svi!2s!4v1703173038543!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Được thiết kế như một tòa lâu đài tráng lệ, mang vẻ đẹp của phong cách đông dương quyến rũ, kết hợp với nghệ thuật trang trí thanh lịch, Sunrise Hotel Bạc Liêu có lối kiến trúc cổ điển sang trọng, hiện diện trong trung tâm hành chính – thương mại – dịch vụ của thành phố Bạc Liêu. Với quy mô 03 tầng có thang máy, với 19 phòng ngủ được trang thiết bị hiện đại gồm các loại phòng :01 Vip, 09 Superior, 06 Deluxe, 03 Tripple.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Phòng VIP nằm ở vị trí đặc biệt nhất khách sạn với góc nhìn đẹp, không gian mở, và lối thiết kế sáng tạo.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Sunrise Hotel Bạc Liêu sở hữu đội ngũ nhân viên trẻ trung năng động, cung cách quản lý chuyên nghiệp sẽ làm hài lòng Quý khách! Đến Sunrise Hotel Bạc Liêu, Quý khách sẽ cảm thấy thoải mái như ở ngôi nhà thứ hai của mình.</span></p>', 1, '2023-12-21 01:37:24', '2023-12-21 03:17:14'),
(12, 11, 'Khách sạn 3 sao Mekong Ecolodge Bungalow', 'Mekong-ecolodge-bungalow', 'http://127.0.0.1:8000/uploads/2023/12/cai-be.jpg', 'http://127.0.0.1:8000/uploads/2023/12/cai-be-1.jpg,http://127.0.0.1:8000/uploads/2023/12/cai-be1.jpg,http://127.0.0.1:8000/uploads/2023/12/cai-be3.jpg', '02733780378', 'Nằm trên cù lao Tân Phong thuộc huyện Cai Lậy, tỉnh Tiền Giang. Đối diện chợ nổi Cái Bè', 'từ 800.000 VNĐ', 'Khách sạn', 'Bắt đầu nhận phòng : 13:00, kết thúc nhận phòng 14:00 hôm sau', 'Được xây dựng trên một hòn đảo xinh đẹp bao quanh là vườn cây ăn trái quanh năm trĩu quả, người dân đôn hậu, hiền lành và mến khách.\r\n\r\nLấy cảm hứng từ thiên nhiên, chúng tôi xây dựng Mekong Ecolodge  hoàn toàn từ nguyên liệu tự nhiên dân dã với mái lá, nội thất tre, gỗ.... Mỗi bungalow được thiết kế theo phong cách nhà dân của địa phương, mang đậm nét văn hóa của vùng Đồng bằng Sông Cửu Long. \r\n\r\nMekong Ecolodge là một thế giới hoàn toàn khác so với nếp sống thành thị nhộn nhịp và ồn ào, không khí trong lành, thiên nhiên hoang sơ và mát mẽ. Sự vắng mặt của các cơ sở vui chơi giải trí khác giữ cho nhà nghỉ của chúng tôi có được sự yên bình, tĩnh lặng.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d7750.407779671891!2d106.04340272055734!3d10.3205054229863!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310a9bef53f8a191%3A0xd71b6f291b014533!2sMekong%20Ecolodge!5e0!3m2!1svi!2s!4v1703173744839!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p class=\"zYHGB\" style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Tìm kiếm khu nghỉ dưỡng lãng mạn lý tưởng ở Cái Bè không phải là quá khó. Chào đón bạn đến với Mekong Lodge, một lựa chọn đúng đắn cho những du khách như bạn.</span></p>\r\n<p class=\"zYHGB\" style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Các phòng khách sạn cung cấp điều hòa nhiệt độ và khu ghế ngồi, và Mekong Lodge làm cho việc kết nối internet trở nên dễ dàng vì internet miễn phí có sẵn.</span></p>\r\n<p class=\"zYHGB\" style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Bạn cũng có thể tận hưởng một số tiện nghi được cung cấp bởi khu nghỉ dưỡng, bao gồm bàn lễ tân 24 giờ, đổi tiền và nhận phòng / trả phòng nhanh. Hơn nữa, khách có thể tận hưởng bể bơi và bữa sáng trong suốt thời gian ở đây. Một điểm thuận tiện nữa đó là bãi đỗ xe có sẵn cho khách.</span></p>\r\n<p class=\"zYHGB\" style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Mekong Lodge sẽ giúp bạn khám phá Cái Bè một cách nhanh chóng và dễ dàng nhất. Kì nghỉ của bạn sẽ trở nên thư giãn và dễ chịu.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Nằm ở huyện Cái Bè, Mekong Ecolodge Bungalow có xe đạp cho khách sử dụng miễn phí, quầy bar, khu vườn và WiFi miễn phí trong toàn bộ khuôn viên. Resort 3 sao này cũng có bàn đặt tour và phòng giữ hành lý. Chỗ nghỉ cung cấp dịch vụ lễ tân 24 giờ, dịch vụ phòng và dịch vụ thu đổi ngoại tệ cho khách. Resort phục vụ bữa sáng kiểu Á hàng ngày. Đi bộ đường dài và đạp xe là các hoạt động được ưa chuộng trong khu vực trong khi du khách có thể thuê xe hơi tại Mekong Ecolodge Bungalow. Chỗ nghỉ nằm cách thành phố Bến Tre 36 km và thành phố Mỹ Tho 33 km. Sân bay gần nhất là sân bay quốc tế Cần Thơ, cách đó 81 km và Mekong Ecolodge Bungalow cung cấp dịch vụ đưa đón sân bay với một khoản phụ phí.</span></p>', 1, '2023-12-21 01:44:43', '2023-12-21 03:17:26'),
(13, 12, 'FAMI Nguyễn Homestay Châu Đốc', 'homestay-chau-doc', 'http://127.0.0.1:8000/uploads/2023/12/hotel-tram-su-2.jpg', 'http://127.0.0.1:8000/uploads/2023/12/hotel-tram-su-3.jpg,http://127.0.0.1:8000/uploads/2023/12/hotel-tram-su1-1.jpg,http://127.0.0.1:8000/uploads/2023/12/hotel-tram-su2-1.jpg,http://127.0.0.1:8000/uploads/2023/12/hotel-tram-su3-1.jpg', '02963571333', 'Tân lộ kiều lương, tổ 8, khóm Châu Thới 1, phường Châu Phú B, thành phố Châu Đốc, Tỉnh An Giang', '300.000đ - 800.000đ', 'Homestay', 'Bắt đầu nhận phòng : 13:00, kết thúc nhận phòng 14:00 hôm sau', 'Homestay FaMi Nguyễn Châu Đốc là một homestay kết hợp lưu trú và du lịch sinh thái nổi tiếng tại Châu Đốc, An Giang. Được xây dựng trên khu đất rộng hơn 1 hecta, Homestay FaMi Nguyễn Châu Đốc bao gồm khu bungalow riêng biệt có diện tích phù hợp với nhóm du khách từ 2-8 người phù hợp với cặp đôi, gia đình và nhóm bạn.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m14!1m8!1m3!1d15681.883088093511!2d105.0946171!3d10.6981254!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310a27ecf9ed1ff3%3A0xb284be9c2a3f85e9!2zTmjDoCBuZ2jhu4kgRmFtaSBOZ3V54buFbg!5e0!3m2!1svi!2s!4v1703174647876!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\"><strong>Homestay FaMi Nguyễn Châu Đốc</strong> là một homestay kết hợp lưu trú và du lịch sinh thái nổi tiếng tại Châu Đốc, An Giang. Được xây dựng trên khu đất rộng hơn 1 hecta, Homestay FaMi Nguyễn Châu Đốc bao gồm khu bungalow riêng biệt có diện tích phù hợp với nhóm du khách từ 2-8 người phù hợp với cặp đôi, gia đình và nhóm bạn.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Trong khuôn viên<strong> Homestay FaMi Nguyễn Châu Đốc</strong> còn có một nông trại nho nhỏ với nhà lồng trồng rau, dưa lưới, dưa hoàng kim,... khu vườn ngoài trời trồng hoa, cây thuốc, cây cảnh, hồ nước ngọt để nuôi cá và vịt, khu nhà hàng sức chứa đến 100 người và sân chơi cầu lông dành cho những du khách yêu thể thao.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Mỗi phòng của<strong> Homestay FaMi Nguyễn Châu Đốc </strong>đều có đầy đủ tiện nghi, sạch sẽ, có nhà vệ sinh riêng, giá chỉ dao động từ 300.000đ - 800.000đ tùy sức chứa. Khu nhà hàng có phục vụ ăn sáng, trưa, tối với những nguyên liệu “nhà trồng” vô cùng tươi ngon, hấp dẫn. Ngoài ra, nếu bạn không có nhu cầu ở qua đêm thì vẫn có thể đến đây tham quan, dã ngoại trong ngày.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\"><strong>Homestay FaMi Nguyễn Châu Đốc</strong> nằm ngay tại trung tâm TP. Châu Đốc, An Giang nên rất thuận tiện di chuyển đến các điểm tham quan nổi tiếng như: Miếu Bà Chúa Xứ Núi Sam, Cáp treo Núi Sam, Làng Chăm Đa Phước, Thánh Đường Hồi giáo JAMIUL AZHAR MOSQUE, Chùa Hang, Chùa Lầu, Cánh đồng hoa Thiên Anh,... Và nhiều quán ăn, tụ điểm ăn chơi nổi tiếng tại thành phố này.</span></p>', 1, '2023-12-21 02:04:13', '2023-12-21 03:17:36'),
(14, 13, 'Khách sạn Phước Thành IV', 'khach-san-phuong-thanhIV', 'http://127.0.0.1:8000/uploads/2023/12/vinh-lonh.jpg', 'http://127.0.0.1:8000/uploads/2023/12/vinh-lonh-1.jpg,http://127.0.0.1:8000/uploads/2023/12/vinh-lonh1.jpg,http://127.0.0.1:8000/uploads/2023/12/vinh-lonh2.jpg,http://127.0.0.1:8000/uploads/2023/12/vinh-lonh3.jpg', '02703868779', '118 120 Nguyễn Huệ, Phường 2, Vĩnh Long, 85100, Việt Nam', 'từ 330.000 VNĐ', 'Khách sạn', 'Giờ nhận phòng 14:00', 'Phuoc Thanh IV Hotel Vinh Long nằm trong bán kính 3.3 km từ Du Lich Trường Huy, cung cấp WiFi ở khắp khách sạn. Từ khách sạn bạn đi 15 phút là đến trung tâm của Vinh Long.Phuoc Thanh IV Hotel Vinh Long nằm trong bán kính 3.3 km từ Du Lich Trường Huy, cung cấp WiFi ở khắp khách sạn. Từ khách sạn bạn đi 15 phút là đến trung tâm của Vinh Long.Phuoc Thanh IV Hotel Vinh Long nằm trong bán kính 3.3 km từ Du Lich Trường Huy, cung cấp WiFi ở khắp khách sạn. Từ khách sạn bạn đi 15 phút là đến trung tâm của Vinh Long.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15704.70013225645!2d105.94954604874209!3d10.247463237933497!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310a83255b381b13%3A0xde2a66532934c802!2sPhuoc%20Thanh%20IV%20Hotel%20V%C4%A9nh%20Long!5e0!3m2!1svi!2s!4v1703175098175!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p class=\"zYHGB\" style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Tìm kiếm một khách sạn lý tưởng ở Vĩnh Long không phải là quá khó. Chào đón bạn đến với Phuoc Thanh IV Hotel, một lựa chọn đúng đắn cho những du khách như bạn.</span></p>\r\n<p class=\"zYHGB\" style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Các phòng khách sạn cung cấp điều hòa nhiệt độ và tủ lạnh, và online là có thể vì wifi miễn phí có sẵn, cho phép bạn thư giãn nghỉ ngơi không phải lo lắng gì.</span></p>\r\n<p class=\"zYHGB\" style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Phuoc Thanh IV Hotel cung cấp bàn lễ tân 24 giờ, bảo quản hành lý và nhận phòng / trả phòng nhanh. Hơn nữa, là khách hàng giá trị của Phuoc Thanh IV Hotel, bạn có thể tận hưởng nhà hàng, có sẵn tại khách sạn. Khách đi đến khách sạn bằng phương tiện của mình có thể dùng bãi đỗ xe riêng miễn phí gần đó.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"> + Dịch vụ tuyệt vời, cơ sở vật chất hoàn chỉnh và các tiện nghi khách sạn cung cấp sẽ khiến quý khách không thể phàn nàn trong suốt kỳ lưu trú tại Phuoc Thanh IV Hotel.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"> + Nhận ưu đãi đặc biệt dành cho các liệu pháp spa tinh tuý nhất giúp thư giãn tinh thần và làm tươi trẻ cơ thể.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"> + Sóng WiFi phủ khắp các khu vực chung của khách sạn cho phép quý khách luôn kết nối với gia đình và bè bạn.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"> + Phuoc Thanh IV Hotel là khách sạn sở hữu đầy đủ tiện nghi và dịch vụ xuất sắc theo nhận định của hầu hết khách lưu trú.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\"> + Với những tiện nghi sẵn có Phuoc Thanh IV Hotel thực sự là một nơi lưu trú hoàn hảo</span></p>', 1, '2023-12-21 02:11:50', '2023-12-21 03:18:23'),
(15, 14, 'Khách sạn Thiên Ân', 'khach-san-thien-san', 'http://127.0.0.1:8000/uploads/2023/12/khach-san-thien-an.jpg', 'http://127.0.0.1:8000/uploads/2023/12/khach-san-thien-an1.jpg,http://127.0.0.1:8000/uploads/2023/12/khach-san-thien-an2.jpg,http://127.0.0.1:8000/uploads/2023/12/khach-san-thien-an3.jpg', '02996567666', '593 Trần Hưng Đạo, Phường 3, Sóc Trăng, Việt Nam', 'từ 500.000 VNĐ', 'Khách sạn', 'Giờ nhận phòng 14:00', 'Khách sạn Thiên Ân kính tặng những quý khách hàng đầu tiên sử dụng dịch vụ: giảm 20% giá phòng đến hết năm 2021\r\nTọa lạc ở một vị trí tuyệt đẹp trên đường Trần Hưng Đạo ngay trung tâm thành phố Sóc Trăng. Khách sạn gồm 48 phòng ngủ sang trọng với đầy đủ trang thiết bị tiện nghi theo tiêu chuẩn quốc tế. Nhà hàng và hồ bơi là điểm nhấn với hệ thống thực đơn phong phú và phục vụ chuyên nghiệp.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15736.473295770584!2d105.95369184860267!3d9.585068048001217!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a053850f3950ab%3A0x4f5720e70dbddd07!2zS2jDoWNoIFPhuqFuIFRoacOqbiDDgm4!5e0!3m2!1svi!2s!4v1703175790863!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Sóc Trăng - một trong những điểm đến thú vị của dân Phượt, là nơi giao thoa văn hóa giữa các dân tộc Hoa, Kinh, Khmer và người Chăm bản địa, do vậy nơi đây có những tín ngưỡng và phong tục độc đáo. Để du khách thỏa sức khám phá, họ thường lựa chọn những nơi lưu trú, khách sạn ở khu vực trung tâm, tiện nghi và hiện đại, trong đó có Hotel Thiên Ân Sóc Trăng.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Khách sạn tọa lạc ở vị trí tuyệt đẹp trên đường Trần Hưng Đạo ngay trung tâm thành phố, thuận tiện cho du khách khám phá các điểm du lịch nổi tiếng và ẩm thực các dân tộc. Chính vì thế, du khách ghé miền Tây luôn lựa chọn Hotel Thiên Ân là điểm dừng chân, vừa được nghỉ dưỡng thoải mái, tận hưởng các tiện nghi hiện đại như hồ bơi ngoài trời, quầy bar nhà hàng,..</span></p>', 1, '2023-12-21 02:23:17', '2023-12-21 03:18:45');
INSERT INTO `hotels` (`id`, `location_id`, `name`, `slug`, `thumnail`, `slides`, `phone`, `address`, `price`, `type`, `times`, `description`, `maps`, `detail`, `status`, `created_at`, `updated_at`) VALUES
(16, 15, 'Sang Như Ngọc Resort', 'resort-sang-nhu-ngoc', 'http://127.0.0.1:8000/uploads/2023/12/resort-nhu-ngoc.jpg', 'http://127.0.0.1:8000/uploads/2023/12/resort-nhu-ngoc1.jpg,http://127.0.0.1:8000/uploads/2023/12/resort-nhu-ngoc2.jpg,http://127.0.0.1:8000/uploads/2023/12/resort-nhu-ngoc3.jpg', '0943333333', 'An Hảo, Thị xã Tịnh Biên, An Giang, Việt Nam', 'từ 800.000 VNĐ', 'Resort', 'Giờ nhận phòng 14:00', 'Sang Như Ngọc Resort là một khu nghỉ dưỡng đầy hiện đại nhưng vẫn gần gũi với thiên nhiên. Sở hữu cảnh quan tuyệt đẹp của Núi Cấm – An Giang, Resort Sang Như Ngọc Núi Cấm hiện lên như một nét chấm phá tinh tế giữa bản hoà ca thiên nhiên đầy màu sắc. Khu nghỉ dưỡng này mang đến cho du khách đầy đủ các tiện nghi hấp dẫn từ nhà hàng, hồ bơi...', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15692.236009790331!2d105.0145182487968!3d10.496015134177828!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3109f7bbbfc601fb%3A0x59fae91388a4b6cf!2zUmVzb3J0IFNhbmcgTmjGsCBOZ-G7jWM!5e0!3m2!1svi!2s!4v1703176249714!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Sang Như Ngọc Resort có địa chỉ tại An Hảo, Tịnh Biên, An Giang. Đây là một vị trí lý tưởng và thuận lợi dành cho du khách khi cách khu vực trung tâm của tỉnh An Giang khoảng hơn 20km. </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Để đến với Resort Sang Như Ngọc Châu Đốc, du khách di chuyển từ Thành phố Hồ Chí Minh sẽ mất khoảng hơn 4 tiếng đồng hồ lái xe. Hoặc nếu du khách di chuyển bằng đường hàng không, sau khi đáp chuyến bay xuống sân bay Cần Thơ có thể book taxi di chuyển thêm 2 tiếng đồng hồ nữa là có thể đến với  Sang Như Ngọc Resort</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">esort Sang Như Ngọc được thiết kế theo phong cách truyền thống, hoà mình với thiên nhiên. Phong cách kiến trúc độc đáo, là sự kết hợp hoàn hảo giữa nét văn hoá đặc trưng của miền Tây với hơi hướng Nhật Bản.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Các phòng nghỉ được thiết kế trong những căn bungalow, xung quanh phòng nghỉ là cây cối bao bọc giúp du khách có thể trải nghiệm và tận hưởng những giây phút yên bình, thoải mái.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Phù hợp với những du khách đi theo gia đình hay đi theo nhóm đông, với diện tích 76 mét vuông rộng rãi, thoáng mát. Căn hộ gồm 2 phòng tắm và 2 phòng ngủ, sẽ giúp du khách có được không gian thoải mái, riêng tư. Căn hộ cũng có đầy đủ các tiện nghi, có hướng núi với ban công mái hiên để ta có thể tận hưởng không khí trong lành cùng cảnh quan tuyệt đẹp của khu nghỉ dưỡng.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Với những du khách đi theo gia đình cũng có thể xem đây là một lựa chọn hoàn hảo khác, với diện tích 72 mét vuông, được trang bị các nội thất, tiện nghi. Đặc biệt, căn hộ còn có khu vực làm việc riêng dành cho máy tính xách tay, giúp những du khách có thể thực hiện công việc của mình mà vẫn được tận hưởng một kì nghỉ dưỡng trọn vẹn tại đây.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Đây là hệ thống phòng phù hợp với những nhóm khách du lịch muốn trải nghiệm một không gian ấm cúng với, quây quần bên nhau, phòng có diện tích 42 mét vuông được thiết kế với công năng gồm 2 giường đơn hoặc một giường đôi, phù hợp với nhu cầu khác nhau của du khách, phòng có hướng nhìn ra vườn với cây cối xanh mát cùng những tiện nghi tiêu chuẩn.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 16pt;\">Đây là một sự lựa chọn tốt cho những nhóm du khách có nhu cầu cơ bản về dịch vụ và tiện ích. Căn phòng có diện tích 35 mét vuông với 3 giường đôi. Phòng có hướng nhìn ra sân vườn giúp có được nguồn ánh sáng tự nhiên cùng những cây cối xanh mát để du khách có được không gian trong lành, thoải mái.</span></p>', 1, '2023-12-21 02:30:54', '2023-12-21 03:18:55'),
(17, 16, 'Hotel Trí Lê', 'hotel-tri-le', 'http://127.0.0.1:8000/uploads/2023/12/hotel-tri-le.jpg', 'http://127.0.0.1:8000/uploads/2023/12/hotel-tri-le1.jpg,http://127.0.0.1:8000/uploads/2023/12/hotel-tri-le2.jpg,http://127.0.0.1:8000/uploads/2023/12/hotel-tri-le3.jpg', '0911909797', 'Vincom Shophouse Cao Lãnh Đường 30/4 Phường 1, thành phố Cao Lãnh, tỉnh Đồng Tháp, Cao Lãnh', 'tù 300.000đ', 'Khách sạn', 'Giờ nhận phòng 14:00', 'Tọa lạc tại thành phố Cao Lãnh, trong một tòa nhà có từ năm 2020, Hotel Trí Lê cung cấp xe đạp cho khách sử dụng miễn phí và phòng nghỉ với WiFi miễn phí. Khách sạn 4 sao này có máy ATM và dịch vụ tiền sảnh. Chỗ nghỉ cung cấp dịch vụ lễ tân 24 giờ, dịch vụ phòng và dịch vụ thu đổi ngoại tệ cho khách.\r\n\r\nPhòng nghỉ gắn máy điều hòa tại Hotel Trí Lê có bàn làm việc, ấm đun nước, minibar, két an toàn, TV màn hình phẳng và phòng tắm riêng với vòi xịt/chậu rửa vệ sinh. Mỗi phòng đều được trang bị ga trải giường và khăn tắm.\r\nChỗ nghỉ cung cấp dịch vụ ủi cùng tiện nghi văn phòng như máy fax và máy photocopy.\r\nThành phố Cần Thơ nằm cách Hotel Trí Lê 50 km khi thành phố Long Xuyên cách đó 25 km. Sân bay gần nhất là sân bay quốc tế Cần Thơ, nằm trong bán kính 43 km từ khách sạn, và chỗ nghỉ cung cấp dịch vụ đưa đón sân bay với một khoản phụ phí.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15694.245792753964!2d105.62428684878799!3d10.456332434776607!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310a658e57e5e0e5%3A0x791e4db0b66b438!2zS2jDoWNoIHPhuqFuIFRyw60gTMOq!5e0!3m2!1svi!2s!4v1703177016725!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p style=\"text-align: justify;\"><span style=\"font-size: 16pt;\">Khách sạn có đội ngũ nhân viên phục vụ 24/7, luôn sẵn lòng giúp đỡ để đảm bảo mọi nhu cầu của quý khách được đáp ứng. Các phòng nghỉ rộng rãi và tiện nghi, một số có ban công với tầm nhìn hùng vĩ ra thành phố. Mọi phòng đều được trang bị TV màn hình phẳng, điều hòa không khí và phòng tắm riêng với vòi sen và đồ vệ sinh cá nhân miễn phí.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 16pt;\">Khám phá các hoạt động giải trí và giải lao tại Cao Lãnh , Đồng Tháp thưởng thức những bữa ăn ngon từ dịch vụ phòng hoặc thưởng thức không khí thư giãn tại phòng chờ chung. Hotel Trí Lê cam kết mang đến một trải nghiệm lưu trú đẳng cấp và ấm áp cho quý khách trong chuyến hành trình tại thành phố Cao Lãnh.</span></p>', 1, '2023-12-21 02:48:03', '2023-12-21 03:19:04'),
(18, 17, 'Khách sạn Mường Thanh Luxury Cà Mau', 'muong-thanh-luxury-ca-mau', 'http://127.0.0.1:8000/uploads/2023/12/mung-thanh-ca-mau.jpg', 'http://127.0.0.1:8000/uploads/2023/12/mung-thanh-ca-mau2.jpg,http://127.0.0.1:8000/uploads/2023/12/mung-thanh-ca-mau3.jpg,http://127.0.0.1:8000/uploads/2023/12/mung-thanh-ca-mau1.jpg', '02902228888', 'Khu trung tâm hành chính chính trị, Phường 9, Tp. Cà Mau, Cà Mau 98000, Việt Nam', 'từ 1.200.000VNĐ', 'Khách sạn', 'Giờ nhận phòng 14:00', 'Khách sạn Mường Thanh Luxury Cà Mau – một trong những khách sạn cao cấp hàng đầu khu vực Đồng Bằng sông Cửu Long, tọa lạc tại Khu trung tâm hành chính – chính trị thành phố Cà Mau. Khách sạn là điểm dừng chân lý tưởng cho những chuyến du lịch hay công tác với vị trí thuận tiện để tiếp cận trung tâm thành phố và những điểm du lịch đặc trưng của vùng', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15754.46216651138!2d105.13055004852377!3d9.189108654058543!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a14990e3673ef9%3A0xa2ed310dfe1ab92f!2zTcaw4budbmcgVGhhbmggTHV4dXJ5IEPDoCBNYXUgSG90ZWw!5e0!3m2!1svi!2s!4v1703177616294!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Khách sạn Mường Thanh Cà Mau tọa lạc tại Khu trung tâm Hành Chính – Chính Trị tỉnh Cà Mau, cách trung tâm thành phố khoảng 0.5 km, cách Bến xe tỉnh Cà Mau 2 km, với giao thông thuận lợi đi đến các vùng Nam Bộ. Cho nên khi lưu trú ở đây các du khách sẽ dễ dàng tiếp cận những điểm tham quan du lịch nổi tiếng của thành phố.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Khách sạn Mường Thanh Luxury Cà Mau được xây dựng với tiêu chuẩn 5 sao đầu tiên ở Cà Mau, khách sạn có tổng 177 phòng tiêu chuẩn được trang bị hiện đại đáp ứng nhu cầu của du khách.  </span><br /><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\"><strong>Hạng phòng Deluxe King:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">+ Phòng có diện tích 37m2, gồm 01 giường đôi cỡ lớn 1,8m x 2m, bàn làm việc, phòng tắm đứng. Phòng áp dụng cho 02 người lớn và trẻ dưới 12 tuổi. Tiền phòng bao gồm: Bữa ăn sáng; Hồ bơi, phòng tập Gym.</span></p>\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\"><strong>Hạng phòng Deluxe Twin:</strong></span></h3>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">+ Phòng có diện tích 37m2, gồm 02 giường đơn cỡ nhỏ 1,2m x 2m, bàn làm việc, phòng tắm đứng. Phòng áp dụng cho 02 người lớn và trẻ dưới 12 tuổi. Tiền phòng bao gồm: Bữa ăn sáng; Hồ bơi, phòng tập Gym.</span></p>\r\n<h3><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\"><strong>Hạng phòng Deluxe Triple</strong></span></h3>\r\n<p><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">+ Phòng có diện tích 40m2, gồm 03 giường đơn kích thước nhỏ 1,2m x 2m, bàn làm việc, phòng tắm đứng. Phòng áp dụng cho 03 người lớn và trẻ dưới 12 tuổi. Tiền phòng bao gồm: Bữa ăn sáng; Hồ bơi, phòng tập Gym.</span></p>', 1, '2023-12-21 02:53:44', '2023-12-21 03:19:13'),
(19, 4, 'Bến Tre Riverside Resort', 'ben-tre-resort', 'http://127.0.0.1:8000/uploads/2023/12/hotellangtap1.jpg', 'http://127.0.0.1:8000/uploads/2023/12/langtaphotel.jpg,http://127.0.0.1:8000/uploads/2023/12/langtaphotel1.jpg,http://127.0.0.1:8000/uploads/2023/12/langtaphotel2.jpg,http://127.0.0.1:8000/uploads/2023/12/langtaphotel3.jpg', '02753545454', '708 Đ. Nguyễn Văn Tư, Phường 7, Bến Tre, Việt Nam', 'từ 500.000 VNĐ', 'Resort', 'Bắt đầu nhận phòng : 13:00, kết thúc nhận phòng 14:00 hôm sau', 'Tọa lạc tại thành phố Bến Tre thuộc tỉnh Bến Tre, cách thành phố Vĩnh Long 42 km, Ben Tre Riverside Resort có hồ bơi ngoài trời mở cửa quanh năm và tầm nhìn ra dòng sông. Chỗ nghỉ cũng có nhà hàng phục vụ khách. Resort cung cấp Wi-Fi miễn phí trong tất cả các khu vực và chỗ đỗ xe riêng miễn phí ngay tại khuôn viên.\r\n\r\nBen Tre Riverside Resort cung cấp dịch vụ đưa đón miễn phí từ chỗ nghỉ đến Chợ đêm Bến Tre.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3926.4904029324525!2d106.3471861748745!3d10.221974489895082!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310aa874cc38f465%3A0xa6085911098d5e1!2sBen%20Tre%20Riverside%20Resort!5e0!3m2!1svi!2s!4v1703178836099!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', '<p><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Phòng nghỉ : </span></p>\r\n<p><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">+ Phòng nghỉ hạng sang cao cấp với nhiều lựa chọn hướng nhìn về thành phố hoặc dòng sông Hàm Luông thơ mộng. Ngoài những trang thiết bị đời mới và hiện đại như: giường ngủ rộng rãi, ghế sofa, hạng phòng Suite còn có bồn tắm nằm sang trọng, mang đến cảm giác</span></p>\r\n<div class=\"elementor-element elementor-element-e480a42 elementor-widget-divider--view-line elementor-widget elementor-widget-divider\" data-id=\"e480a42\" data-element_type=\"widget\" data-settings=\"{\" data-widget_type=\"divider.default\">\r\n<div class=\"elementor-widget-container\">\r\n<div class=\"elementor-divider\">\r\n<div class=\"elementor-element elementor-element-90a9601 elementor-widget elementor-widget-text-editor\" data-id=\"90a9601\" data-element_type=\"widget\" data-settings=\"{\" data-widget_type=\"text-editor.default\">\r\n<div class=\"elementor-widget-container\">\r\n<p><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Nhà hàng Hoàng Lam</span></p>\r\n<p><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">+ Nhà hàng Hoàng Lam với đội ngũ đầu bếp chuyên nghiệp, sáng tạo luôn mang đến cho du khách sự hài lòng với nhiều thực đơn phong phú, đa dạng từ những món đặc trưng xứ Dừa và miền Tây đến những món Á – Âu.</span></p>\r\n<p><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Phòng hội nghị : </span></p>\r\n<p><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">+ Sở hữu không gian rộng rãi, từ trong nhà và ngoài trời, cùng thiết kế sang trọng, resort là điểm lý tưởng cho mọi sự kiện và hội nghị kèm đầy đủ tiện ích hiện đại</span></p>\r\n<p><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">Tiệc ngoài trời : </span></p>\r\n<p><span style=\"font-size: 16pt; font-family: \'times new roman\', times, serif;\">+ Không gian tiệc thoáng mát và lãng mạn bên bờ sông <strong>Hàm Luông thơ mộng cho mọi bữa tiệc đẳng cấp.</strong></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', 1, '2023-12-21 03:14:26', '2023-12-21 03:19:34');

-- --------------------------------------------------------

--
-- Table structure for table `locations`
--

CREATE TABLE `locations` (
  `id` int UNSIGNED NOT NULL,
  `categories_id` int NOT NULL DEFAULT '0',
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `thumnail` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `slides` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `address` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `description` text COLLATE utf8mb4_unicode_ci,
  `maps` text COLLATE utf8mb4_unicode_ci,
  `price` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `detail` longtext COLLATE utf8mb4_unicode_ci,
  `status` tinyint DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Dumping data for table `locations`
--

INSERT INTO `locations` (`id`, `categories_id`, `name`, `slug`, `thumnail`, `slides`, `address`, `description`, `maps`, `price`, `detail`, `status`, `created_at`, `updated_at`) VALUES
(1, 1, 'Khu du lịch Cồn Phụng', 'con-phung', 'http://127.0.0.1:8000/uploads/2023/12/screenshot-2023-12-19-232248.png', 'http://127.0.0.1:8000/uploads/2023/12/screenshot-2023-12-19-232259.png,http://127.0.0.1:8000/uploads/2023/12/screenshot-2023-12-19-232308.png', 'Huyện Châu Thành, Tỉnh Bến Tre', 'Mô tả ngắn gọn : Khu du lịch Cồn Phụng còn được biết đến với tên gọi là cồn Tân Vinh, cồn có tổng diện tích hơn 30.000m2. Cồn Phụng thuộc một trong 4 vùng đất tứ Linh trên sông Tiền là Long, Lân, Quy, Phụng. Mỗi năm khu du lịch Cồn Phụng tiếp đón hàng chục nghìn du khách trên khắp cả nước ghé thăm.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15700.42929649947!2d106.3461648458304!3d10.333295586635074!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310aaf836b25af7f%3A0xc4aa5170f4b14544!2zY-G7k24gUGjhu6VuZw!5e0!3m2!1svi!2s!4v1702713866413!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Miễn phí', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 13pt;\">Cồn Phụng hiện đang là một địa điểm tham quan không thể bỏ lỡ khi tới du lịch Bến Tre. Là một cù lao nằm trên con sông Tiền thơ mộng, bất cứ ai đặt chân lên mảnh đất Cồn Phụng đều có một cảm giác bình yên, thoải mái và thú vị đến lạ thường. Bởi lẽ khi xuôi mái thuyền trên những dòng sông, cả một vùng miền Tây Nam Bộ hiện ra trước mắt đẹp đẽ, vừa thân thuộc, vừa độc đáo đến lạ thường. Một chuyến du lịch Cồn Phụng đem tới cho bạn những trải nghiệm thú vị. Là một cù lao nằm trên con sông Tiền thơ mộng, bất cứ ai đặt chân lên mảnh đất Cồn Phụng đều có một cảm giác bình yên, thoải mái và thú vị đến lạ thường. Bởi lẽ khi xuôi mái thuyền trên những dòng sông, cả một vùng miền Tây Nam Bộ hiện ra trước mắt đẹp đẽ, vừa thân thuộc, vừa độc đáo đến lạ thường. Một chuyến du lịch Cồn Phụng đem tới cho bạn những trải nghiệm thú vị.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 13pt;\">Được ông trời ban cho thời tiết nắng ấm quanh năm, Cồn Phụng dang tay đón tiếp khách du lịch vào bất kì thời điểm nào trong năm. Đặc biệt vào những ngày hè tháng 6, 7, 8, khi cây lá xanh tươi mơn mởn, các loại quả kết trái căng mọng trên cây, khách du lịch tới với Cồn Phụng sẽ được thưởng thức những loại quả vô cùng ngon lành và phong phú: Măng cụt, sầu riêng, chôm chôm,…</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 13pt;\">Với những du khách muốn trải nghiệm cảm giác phượt tới một vùng đất mới thì có thể lựa chọn những phương tiện cá nhân như xe máy, ô tô để di chuyển. Đối với phương tiện cá nhân sẽ giúp bạn có cơ hội chủ động, dễ dàng trong việc chinh phục và khám phá những địa điểm nơi đây và chi phí sẽ rẻ hơn rất nhiều so với việc đi xe khách.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 13pt;\">Cồn Phụng còn được biết đến với tên gọi là cù lao Đạo Dừa. Tên gọi này bắt nguồn từ giáo phái do ông Nguyễn Thành Nam thành lập nên khi xây chùa Nam Quốc Phật. Hàm nghĩa của tên gọi Đạo Dừa cũng giống như chủ trương giáo phái đó là hy vọng vùng đất này luôn bình yên.</span></p>', 1, '2023-12-15 17:23:45', '2024-01-06 09:03:37'),
(3, 3, 'Danh lam thắng cảnh Ao Bà Ôm', 'ao-ba-om', 'http://127.0.0.1:8000/uploads/2023/12/tv01.jpg', 'http://127.0.0.1:8000/uploads/2023/12/tv02.jpg,http://127.0.0.1:8000/uploads/2023/12/tv03.jpg', 'Quốc lộ 53, thuộc Phường 8 thành phố Trà Vinh, tỉnh Trà Vinh', 'Nhắc tới Trà Vinh, người ta nghĩ đến mảnh đất của những ngôi chùa Khmer cổ kính cùng những di tích lịch sử mang nhiều truyền thuyết huyền thoại, gắn liền với hành trình khai phá, gầy dựng phương Nam. Di tích ao Bà Om ngàn năm soi bóng cổ tự là một trong những niềm tự hào của người dân nơi đây.Du lịch Trà Vinh đến đây, bạn không đơn thuần chỉ được chiêm ngưỡng vẻ đẹp thơ mộng của thiên nhiên mà còn được cảm nhận rõ nét hơn những giá trị văn hoá độc đáo của vùng đất Trà Vinh qua câu chuyện mang đậm màu sắc Khmer Nam Bộ…', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d7860.392329158866!2d106.2988795915199!3d9.917614460734233!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a0171f20bc97cb%3A0x6c42101d0d79f10d!2zQW8gQsOgIE9t!5e0!3m2!1svi!2s!4v1702830328202!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Miễn phí', '<p style=\"text-align: justify;\">Ấn tượng đầu tiên khi tới thăm ao Bà Om là cảm giác mát mẻ trước cảnh trời nước xanh biếc một màu. Ao có hình chữ nhật, rộng 300m, dài 500m (vì gần với hình vuông nên còn được gọi là Ao Vuông) khách sẽ bất ngờ vì ao lớn quá, phải gọi là hồ thì đúng hơn.</p>\r\n<p style=\"text-align: justify;\">Bao bọc xung quanh trên bờ ao là rừng cây cổ thụ, đa số là cây sao, cây dầu hàng trăm năm tuổi, có rễ xù xì trồi lên mặt đất cả mét thành những hình thù lạ mắt bốn mùa rợp bóng thâm u tạo không gian thanh bình, yên tĩnh.</p>\r\n<p style=\"text-align: justify;\">Có bộ rễ lớn đến nỗi có thể tạo thành cái hang độc nhất vô nhị, trẻ con có thể chui vào vui chơi. Lại có bộ rễ cây trở thành ghế ngồi nghỉ chân của khách. Người bản xứ lý giải rằng, những bộ rễ đồ sộ, quấn lấy nhau và nằm trên cao cách mặt đất như ngày nay là do khu đất xung quanh ao bị sụt lún xuống thấp, rễ cây lộ thiên và phát triển theo thời gian.</p>\r\n<p style=\"text-align: justify;\">Kích cỡ khổng lồ và hình thù kỳ dị của các bộ rễ cây ở ao Bà Om khiến nhiều người liên tưởng đến một cánh rừng cổ tích.</p>\r\n<p style=\"text-align: justify;\">Ngoài cảnh đẹp đến mê mẩn lòng người, ao nước rộng lớn này còn lung linh huyền ảo bởi những câu chuyện nửa hư nửa thực từ bao đời nay ăn sâu vào tiềm thức người dân địa phương.</p>', 1, '2023-12-17 02:25:39', '2024-01-06 08:49:42'),
(4, 3, 'Làng nổi Tân Lập', 'lang-noi-tan-lap', 'http://127.0.0.1:8000/uploads/2023/12/la1.jpg', 'http://127.0.0.1:8000/uploads/2023/12/la1-2.jpg,http://127.0.0.1:8000/uploads/2023/12/la2-2.jpg,http://127.0.0.1:8000/uploads/2023/12/lang-noi-tan-lap-4-1.jpg,http://127.0.0.1:8000/uploads/2023/12/lang-noi-tan-lap-22-1.jpg', 'Quốc Lộ 62, xã Tân Lập, huyện Mộc Hóa, tỉnh Long An', 'Làng nổi Tân Lập nằm ở huyện Mộc Hóa, tỉnh Long An là điểm đến đầu tiên được nhiều người yêu thích nhất. Có thể nói đây là địa điểm du lịch nổi lên nhờ vào thiên thời, địa lợi, nhân hòa rất đúng. Thiên thời, Làng Nổi Tân Lập có diện tích 135 ha được phủ bởi thảm thực vật rừng tràm nguyên. Địa lợi, bạn di chuyển đến địa điểm du lịch này rất dễ dàng, bạn có thể di chuyển bằng nhiều hình thức xe máy, xe du lịch...', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15681.016609460317!2d105.97708934884604!3d10.714869930881214!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310aef7085439879%3A0xdb8d266f16711875!2zTMOgbmcgTuG7lWkgVMOibiBM4bqtcA!5e0!3m2!1svi!2s!4v1702830690522!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Miễn phí', '<p style=\"text-align: justify;\">Các bạn đừng hiểu lầm <a class=\"auto-link\" title=\"làng nổi Tân Lập\" href=\"https://thamhiemmekong.com/thong-tin-du-lich-mien-tay/long-an/lang-noi-tan-lap-diem-du-lich-noi-tieng-tai-long.html\" target=\"_top\">làng nổi Tân Lập</a> bên trong có một ngôi làng nhé, nơi đây là một khu rừng tràm nguyên sinh rộng lớn và những con đường bí ẩn dẫn vào rừng. Đây được xem là địa điểm phù hợp dành cho những ai thích tìm về với thiên nhiên hoang dã, khám phá nét văn hoá sông nước miền Tây Nam Bộ.</p>\r\n<p style=\"text-align: justify;\">Sở dĩ có cái tên Làng nổi Tân Lập là do trước kia khi chưa được quy hoạch, vào mùa nước nổi khoảng tháng 7 âm lịch hàng năm người dân ở đây thường nâng cao sàn nhà theo con nước lên, nhìn từ xa giống như một làng nổi trên mặt nước mênh mông. Do đó, khi quy hoạch khu du lịch này, tên gọi làng nổi gắn với địa danh xã Tân Lập đã được đặt cho khu du lịch.</p>\r\n<p style=\"text-align: justify;\">Long An với đôi dòng Vàm Cỏ cùng hệ thống sông ngòi chằng chịt, hệ thống rừng tràm và thực vật, động vật phong phú là điều kiện phát triển loại hình du lịch sinh thái. Trong đó nổi tiếng nhất là Khu du lịch sinh thái <a class=\"auto-link\" title=\"Làng nổi Tân Lập\" href=\"https://thamhiemmekong.com/thong-tin-du-lich-mien-tay/long-an/lang-noi-tan-lap-diem-du-lich-noi-tieng-tai-long.html\" target=\"_top\">Làng nổi Tân Lập</a>. Đến <a class=\"auto-link\" title=\"làng nổi Tân Lập\" href=\"https://thamhiemmekong.com/thong-tin-du-lich-mien-tay/long-an/lang-noi-tan-lap-diem-du-lich-noi-tieng-tai-long.html\" target=\"_top\">làng nổi Tân Lập</a>, du khách sẽ có cái nhìn đầy đủ, chân thực về những nét đặc trưng của miền Tây sông nước, của vùng đất ngập nước Đồng Tháp Mười.</p>\r\n<p style=\"text-align: justify;\">Với diện tích 135ha, có vùng đệm rộng 500ha được quy hoạch để xây dựng khu du lịch đặc trưng của Long An nói riêng và cả vùng đất ngập nước Đồng Tháp Mười nói chung. <a class=\"auto-link\" title=\"Du lịch Long An\" href=\"https://thamhiemmekong.com/thong-tin-du-lich-mien-tay/diem-du-lich-long-an.html\" target=\"_top\">Du lịch Long An</a> ghé thăm làng nổi Tân Lập này bạn sẽ được hòa mình vào vùng đất ngập nước đặc trưng với sinh cảnh rừng tràm, sen – súng, lục bình, lúa ma điển hình của khu vực và là nơi cư trú của nhiều loại động vật (chim, cò, cá…)</p>', 1, '2023-12-17 02:31:36', '2024-01-04 01:29:44'),
(5, 3, 'Chợ nổi Cái Răng', 'cai-rang', 'http://127.0.0.1:8000/uploads/2023/12/cn1.jpg', 'http://127.0.0.1:8000/uploads/2023/12/cr2.jpg,http://127.0.0.1:8000/uploads/2023/12/ct1.jpg', 'Phường Lê Bình, Quận Cái Răng, Thành phố Cần Thơ', 'Chợ nổi Cái Răng nằm ở phía hạ lưu sông Cần Thơ, cách cầu Cái răng khoảng 600m với diện tích mặt nước khá rộng lớn, thuận lợi cho hoạt động của chợ nổi: chiều rộng chiếm luồng ngang sông trung bình 100-120m, chiều dọc sông khoảng 1300-1500m; diện tích mặt nước tương đối rộng nằm trên địa bàn quận Cái Răng với khoảng 300-400 ghe họp chợ mỗi ngày. Hình thức chào hàng khá độc đáo ở chợ nổi Cái Răng là sử dụng cây bẹo. Người bán loại hàng nông sản nào thì treo loại hàng đó lên một cây sào trên ghe để chào hàng. Những loại hàng hóa được bày bán trên ghe, thường thì mỗi ghe sẽ chuyên bán một loại mặt hàng. Trước mỗi ghe hàng, thường có một cây xào chống, trên đó treo loại mặt hàng mà ghe có. Ví dụ như, nếu ghe chuyên bán khoai lang thì trên cây xào sẽ treo lủng lẳng vài củ khoai, còn nếu ghe bán xoài thì trên cây xào sẽ có treo vài quả xoài như thể muốn người mua biết rằng “Ở đây có bán khoai” hoặc “Ở đây có bán xoài”.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15716.572409679153!2d105.73568184869!3d10.005036141608365!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a089afb6342d8d%3A0xcdb2b569f5f13898!2zQ2jhu6MgbuG7lWkgQ8OhaSBSxINuZw!5e0!3m2!1svi!2s!4v1702831012550!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Miễn phí', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 13pt;\"><strong>Chợ nổi Cái Răng nằm</strong> ở phía hạ lưu sông Cần Thơ, cách cầu Cái răng khoảng 600m với diện tích mặt nước khá rộng lớn, thuận lợi cho hoạt động của chợ nổi: chiều rộng chiếm luồng ngang sông trung bình 100-120m, chiều dọc sông khoảng 1300-1500m; diện tích mặt nước tương đối rộng nằm trên địa bàn quận Cái Răng với khoảng 300-400 ghe họp chợ mỗi ngày.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 13pt;\">Hình thức chào hàng khá độc đáo ở chợ nổi Cái Răng là sử dụng cây bẹo. Người bán loại hàng nông sản nào thì treo loại hàng đó lên một cây sào trên ghe để chào hàng. Những loại hàng hóa được bày bán trên ghe, thường thì mỗi ghe sẽ chuyên bán một loại mặt hàng. Trước mỗi ghe hàng, thường có một cây xào chống, trên đó treo loại mặt hàng mà ghe có. Ví dụ như, nếu ghe chuyên bán khoai lang thì trên cây xào sẽ treo lủng lẳng vài củ khoai, còn nếu ghe bán xoài thì trên cây xào sẽ có treo vài quả xoài như thể muốn người mua biết rằng “Ở đây có bán khoai” hoặc “Ở đây có bán xoài”. Những ghe như thế gọi là ghe bẹo.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 13pt;\"><strong>Chợ nổi Cái Răng</strong> thường họp khá sớm, thường từ lúc mờ sáng và đến khoảng 8, 9 giờ thì vãn. Ngày xưa, người dân thường dùng xuồng ba lá, xuồng năm lá, ghe tam bản để về họp chợ nổi. Bây giờ có cả tắc ráng, ghe máy. Người đi mua cũng đến chợ bằng xuồng, ghe. Người chèo xuồng như nghệ sĩ uốn dẻo với cây chèo điều khiển những chiếc xuồng con len lỏi khéo léo giữa hàng trăm ghe thuyền mà không hề va quệt. Người miền Nam vốn chân chất, dân thương hồ trên sông nước miền Tây còn chân chất và đáng yêu hơn. Bởi họ phải vất vả mưu sinh trên sông nước hàng ngày, nên họ sống với nhau bằng cái tình của sông, của nước mênh mông và cởi mở. Không cố gắng tranh giành phần thắng, mà lựa nhau, nhìn nhau mà đi, lựa nhau mà bán, nhường nhịn, giúp đỡ, chia sẻ nhau mà sống. Đó là văn hóa tình người, điều làm nên sự đặc sắc của văn hóa chợ nổi Cái Răng.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 13pt;\"> Chợ đông nhất là vào khoảng 7- 8 sáng. Chợ không hoạt động và hoạt động rất ít vào các ngày Tết Âm Lịch (mồng 1 và mồng 2 Tết, Tết Đoan Ngọ (mồng 5 tháng Năm âm lịch). Do nhu cầu của người đi chợ nên không chỉ có các xuồng trái cây, nông sản phẩm mà còn có nhiều loại dịch vụ khác: phở, hủ tiếu, cà phê, quán nhậu nổi... Các xuồng dịch vụ (thường là thuyền nhỏ) len lỏi phục vụ khách đi chợ và cả khách tham quan.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 13pt;\">Sức hút của chợ nổi Cái Răng đối với thập khách chính là giữ gìn và phát huy được nét đặc trưng vùng sông nước và sự tươi ngon của hàng hóa nơi đây, thấy tâm hồn mình rộng mở và khoáng đạt hơn, cảm nhận được sự khác biệt của cuộc sống nơi đây, thấy được sự chân thành, giản dị và hiếu khách hiếm có của những con người vùng sông nước miền Tây nói chung. </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 13pt;\">Không chỉ giữ gìn được nét đẹp văn hóa vùng sông nước, ngày nay chợ nổi như một điểm du lịch hấp dẫn mà du khách, đặc biệt là du khách nước ngoài rất thích khám phá và trải nghiệm. Cứ năm chiếc xuồng vào chợ thì có đến bốn chiếc chở người ngoại quốc, họ tỏ ra hiếu kì và thích thú từ khung cảnh đến con người miền sông nước, thậm chí đến cả câu rao hàng ngọt như “ mía lùi” của những cô gái Tây Đô.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 13pt;\"> Không gian chợ nổi Cái Răng còn là điểm đến hấp dẫn, mang sắc thái địa phương, thu hút nhiều khách du lịch trong và ngoài nước, nâng cao đời sống cộng đồng chủ thể di sản và góp phần phát triển kinh tế - xã hội cho địa phương. Ngày nay, dù mạng lưới giao thông đường bộ đã phát triển rộng khắp nhưng Chợ nổi Cái Răng vẫn tồn tại và phát triển. Địa điểm họp chợ nằm trên tuyến giao thông đi các tỉnh ở phía tây sông Hậu như Cà Mau - Rạch Giá. Đồng thời, nơi này không xa dòng sông Hậu, con đường giao thông thủy liên vùng và quốc tế, cho nên Chợ nổi Cái Răng là nơi hội tụ của dân thương hồ khắp nơi đổ về, tạo nên sắc màu văn hóa đa dạng trên chợ nổi. Không gian văn hóa chợ nổi Cái Răng tích hợp nhiều loại hình di sản văn hóa phi vật thể: tập quán xã hội và tín ngưỡng, tri thức dân gian, nghệ thuật trình diễn dân gian (đờn ca tài tử)… và những di sản này vẫn đang được gìn giữ và lưu truyền. Mặc dù có sự tiếp biến nhưng nhìn chung vẫn còn khả năng duy trì và phát triển.</span></p>', 1, '2023-12-17 02:37:00', '2024-01-04 01:31:57'),
(6, 1, 'Nhà cổ Bình Thủy', 'nha-co-binh-thuy', 'http://127.0.0.1:8000/uploads/2023/12/nha-co-binh-thuy.jpg', 'http://127.0.0.1:8000/uploads/2023/12/nha-co-binh-thuy-1.jpg,http://127.0.0.1:8000/uploads/2023/12/nha-co-binh-thuy1.jpg,http://127.0.0.1:8000/uploads/2023/12/nha-co-binh-thuy3.jpg', '144, Đường Bùi Hữu Nghĩa, Phường Bình Thủy, Quận Bình Thuỷ, Thành phố Cần Thơ', 'Về xứ Tây Đô, ngoài ghé thăm chợ nổi Cái Răng, đến bến Ninh Kiều, một điểm du lịch hấp dẫn khách không thể bỏ lỡ, đó là nhà cổ Bình Thủy. Hơn 100 năm tuổi nhưng ngôi nhà cổ vẫn giữ gần như vẹn nguyên nét kiến trúc độc đáo, là sự giao thoa giữa văn hóa phương Đông và phương Tây. Đến thủ phủ của miền Tây Nam Bộ, níu giữ chân du khách là thiên nhiên ưu đãi với cây trái ngọt lành, cá tôm phong phú. Vẻ đẹp và nét quyến rũ của mảnh đất được xưng danh “Tây Đô” gói gọn trong câu ca dao xưa: “Cần Thơ gạo trắng nước trong/ Ai đi đến đó lòng không muốn về”. Về Cần Thơ, ngoài lịch trình khám phá chợ nổi Cái Răng, chợ Phong Điền, bến Ninh Kiều, đừng quên ghé qua ngôi nhà cổ Bình Thủy được mệnh danh đẹp nhất xứ Tây Đô. Trải qua bom đạn của thời chiến với hơn 100 năm lịch sử, đến nay, ngôi nhà vẫn giữ gần như vẹn nguyên nét kiến trúc độc đáo. Nơi này cũng từng làm bối cảnh của bộ phim “Người tình” của đạo ...', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15713.56389757196!2d105.73928824870316!3d10.067015240667764!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a087681a8673ad%3A0x42943204998f75ed!2zTmjDoCBj4buVIELDrG5oIFRo4buneQ!5e0!3m2!1svi!2s!4v1703142577914!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Miễn phí', '<p style=\"text-align: justify;\"><strong>Về xứ Tây Đô, ngoài ghé thăm chợ nổi Cái Răng, đến bến Ninh Kiều, một điểm du lịch hấp dẫn khách không thể bỏ lỡ, đó là nhà cổ Bình Thủy. Hơn 100 năm tuổi nhưng ngôi nhà cổ vẫn giữ gần như vẹn nguyên nét kiến trúc độc đáo, là sự giao thoa giữa văn hóa phương Đông và phương Tây.</strong></p>\r\n<p style=\"text-align: justify;\">Đến thủ phủ của miền Tây Nam Bộ, níu giữ chân du khách là thiên nhiên ưu đãi với cây trái ngọt lành, cá tôm phong phú. Vẻ đẹp và nét quyến rũ của mảnh đất được xưng danh “Tây Đô” gói gọn trong câu ca dao xưa: “Cần Thơ gạo trắng nước trong/ Ai đi đến đó lòng không muốn về”.</p>\r\n<p style=\"text-align: justify;\">Về Cần Thơ, ngoài lịch trình khám phá chợ nổi Cái Răng, chợ Phong Điền, bến Ninh Kiều, đừng quên ghé qua ngôi nhà cổ Bình Thủy được mệnh danh đẹp nhất xứ Tây Đô. Trải qua bom đạn của thời chiến với hơn 100 năm lịch sử, đến nay, ngôi nhà vẫn giữ gần như vẹn nguyên nét kiến trúc độc đáo. Nơi này cũng từng làm bối cảnh của bộ phim “Người tình” của đạo diễn J.J.Annaud nổi tiếng.</p>\r\n<p style=\"text-align: justify;\">Nhà cổ Bình Thủy hiện đang nằm trên đường Bùi Hữu Nghĩa, được gia đình họ Dương xây dựng lần đầu vào khoảng năm 1870, xây mới vào những năm đầu thế kỷ XX. Chủ nhân đầu tiên của căn nhà là ông Dương Văn Vị xây dựng lên để làm nhà thờ tổ tiên. Hậu nhân của ông là Dương Chấn Kỷ – một thương gia tri thức giàu có, đồng thời là điền chủ với tính thẩm mỹ và sự sáng tạo cao đã tiếp tục trùng tu và phát triển công trình của cha mình để lại. Nét đặc biệt của ngôi nhà khác hẳn với nhiều ngôi nhà cổ Nam Bộ thể hiện qua lối kiến trúc độc đáo, có sự giao thoa mềm mại của hai nền văn hóa Đông – Tây phối hợp.</p>\r\n<p style=\"text-align: justify;\">Khoảng sân phía trước trồng cây cảnh rộng rãi và thoáng mát</p>\r\n<p style=\"text-align: justify;\">Trải qua 2 cuộc chiến tranh khói lửa, khi nhiều công trình xung quanh bị tàn phá, may mắn nhà cổ Bình Thủy gần như vẹn nguyên, được các đời sau nối tiếp bảo vệ và gìn giữ. Chủ nhân hiện tại là ông Dương Minh Hiển. Nằm trên mảnh đất rộng chừng 6000 m2, ngôi nhà cổ theo hướng Đông – Tây, xung quanh bao bọc bởi vườn cây cảnh, cây ăn trái xanh tốt quanh năm, tạo bầu không khí thoáng đãng trong lành.</p>\r\n<p style=\"text-align: justify;\">So với sân vườn, nền nhà cao hơn chừng 1m. Được biết, chủ nhân căn nhà áp dụng kinh nghiệm dân gian xưa, đổ một lớp muối hột dày khoảng 10 cm trước khi lát gạch bông, tạo cảm giác thoáng mát, đồng thời giúp chống mối mọt.</p>\r\n<p style=\"text-align: justify;\">Phong cách phương Tây đậm nét trong căn nhà có thể kể tới nền nhà lát bằng gạch bông nhập từ Pháp, trần trang trí hoa văn, đèn chùm sang trọng, bộ bàn ghế gỗ chế tác theo kiểu Louis XV, đặc biệt là chậu rửa mặt lavabo bằng men sứ trắng hoa xanh đặt trên bệ gỗ… Một trong những điểm đặc biệt khiến du khách ấn tượng nhất khi bước chân vào nhà cổ Bình Thủy chính là bộ sưu tập đồ cổ phong phú. Trong đó, nhiều món đồ cổ quý giá được nhập khẩu từ Trung Quốc hay đưa từ các nước châu Âu.</p>\r\n<p style=\"text-align: justify;\">Tuy vậy, “hơi thở” của một ngôi nhà Nam Bộ vẫn thể hiện rõ qua từng chi tiết nhỏ. Vị trí trang trọng nhất căn nhà là gian thờ thuần Việt. Xung quanh phần gỗ trạm trổ hay các bức phù điêu được chế tác tinh xảo bởi bàn tay những nghệ nhân Việt tài hoa, với nhiều hình thù gần gũi trong đời sống người Nam Bộ như mai, lan, cúc, trúc, sen, long, lân, quy, phụng, tôm, cua, chim trĩ …</p>\r\n<p style=\"text-align: justify;\">Anh Hữu Luật, hướng dẫn viên công ty du lịch Vietravel, chia sẻ với chúng tôi câu chuyện khá thú vị. Xưa kia, công tử Bạc Liêu từng dừng chân ở nhà cổ Bình Thủy. Khi ngắm nhìn cặp ngà voi trưng bày trong nhà, ông đề nghị nhà họ Dương bán lại với bất cứ giá nào. Nhưng cuối cùng, lời đề nghị đã bị gia đình khước từ.</p>\r\n<p style=\"text-align: justify;\"><img src=\"https://scontent.smarttourism.vn/resources/portal/Images/CTO/superadminportal.cto/DiaDiem/NhaCoBinhThuy/nhacobty5_717532529.jpg\" alt=\"\" /></p>', 1, '2023-12-20 17:10:19', '2024-01-06 01:45:24'),
(7, 2, 'Thiền viện trúc lâm Phương Nam', 'thien-vien-truc-lam-phuong-nam', 'http://127.0.0.1:8000/uploads/2023/12/thien-vien-truc-lam.jpg', 'http://127.0.0.1:8000/uploads/2023/12/thien-vien-truc-lam3.jpg,http://127.0.0.1:8000/uploads/2023/12/thien-vien-truc-lam4.jpg,http://127.0.0.1:8000/uploads/2023/12/thien-vien-truc-lam1.jpg', 'Đường Mỹ Nhơn , Xã Mỹ Khánh, Huyện Phong Điền, Thành phố Cần Thơ', 'Thiền viện Trúc Lâm Phương Nam tọa lạc tại xã Mỹ Khánh, huyện Phong Điền, thành phố Cần Thơ, cách làng du lịch Mỹ Khánh vài trăm mét và cách trung tâm thành phố khoảng 15 km. Khách thập phương lần đầu tiên đến đây sẽ không khỏi ngỡ ngàng trước dáng vẻ độc đáo của ngôi chùa mang nét văn hóa kiến trúc thời Lý – Trần. Thiền viện được xây dựng trên diện tích gần 4ha, được khởi công xây dựng vào ngày 16.7.2013 và hoàn thành sau 10 tháng thi công. Ngày 17.5.2014, lễ khánh thành Thiền viện Trúc Lâm Phương Nam đã được Ban tri sự Phật giáo TP. Cần Thơ tổ chức một cách trọng thể. Toàn bộ công trình kết cấu mái lợp ngói, khung cột bằng gỗ lim, vách tường gạch, nền và lối đi đều lát gạch tàu. Ngoài ngôi chính điện, khuôn viên được bài trí cân đối với khoảng 20 hạng mục công trình như: nhà tổ, hội trường rộng rãi làm nơi giảng đạo, tu học cho khoảng 500 Tăng sinh, Phật tử, bảo tháp 9 tầng, tháp trống, tháp chuông bằng đồng, khu tăng xá và nhà khách, khu trai ...', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15717.27917738441!2d105.6936662067875!3d9.990420642488647!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a089466860f259%3A0xfeda73cf27058a98!2zVGhp4buBbiB2aeG7h24gVHLDumMgTMOibSBQaMawxqFuZyBOYW0!5e0!3m2!1svi!2s!4v1703148493325!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Miễn phí', '<p style=\"text-align: justify;\"><strong>Thiền viện Trúc Lâm Phương Nam tọa lạc tại xã Mỹ Khánh, huyện Phong Điền, thành phố Cần Thơ, cách làng du lịch Mỹ Khánh vài trăm mét và cách trung tâm thành phố khoảng 15 km. Khách thập phương lần đầu tiên đến đây sẽ không khỏi ngỡ ngàng trước dáng vẻ độc đáo của ngôi chùa mang nét văn hóa kiến trúc thời Lý – Trần.</strong></p>\r\n<p style=\"text-align: justify;\">Thiền viện được xây dựng trên diện tích gần 4ha, được khởi công xây dựng vào ngày 16.7.2013 và hoàn thành sau 10 tháng thi công. Ngày 17.5.2014, lễ khánh thành Thiền viện Trúc Lâm Phương Nam đã được Ban tri sự Phật giáo TP. Cần Thơ tổ chức một cách trọng thể.</p>\r\n<p style=\"text-align: justify;\">Toàn bộ công trình kết cấu mái lợp ngói, khung cột bằng gỗ lim, vách tường gạch, nền và lối đi đều lát gạch tàu. Ngoài ngôi chính điện, khuôn viên được bài trí cân đối với khoảng 20 hạng mục công trình như: nhà tổ, hội trường rộng rãi làm nơi giảng đạo, tu học cho khoảng 500 Tăng sinh, Phật tử, bảo tháp 9 tầng, tháp trống, tháp chuông bằng đồng, khu tăng xá và nhà khách, khu trai đường, thư viện và phòng thuốc, nhà thủy tạ,… xây bao quanh, tạo thành một quần thể kiến trúc mang đậm ý nghĩa văn hóa lịch sử.</p>\r\n<p style=\"text-align: justify;\">Đặc biệt có những hạng mục được làm bằng gỗ lim, khoảng 1.000 khối được nhập từ Nam Phi. Riêng tượng Phật Thích Ca Mâu Ni thờ trong chánh điện được làm bằng đồng, nặng 3,5 tấn, đại hồng chung nặng 1,5 tấn. Tượng Bồ Tát và các vị tổ sư được tạc bằng gỗ Du Sam có tuổi thọ 800 năm.</p>\r\n<p style=\"text-align: justify;\">Đặc biệt, xung quanh thiền viện có rất nhiều điểm vườn du lịch. Sự kết hợp giữa những khu du lịch sinh thái tại Phong Điền với Thiền viện Trúc Lâm Phương Nam sẽ tạo ra sản phẩm du lịch “Sinh thái – hành hương”. Du khách được thưởng thức vẻ đẹp của vùng sông nước, được lắng mình trong tiếng chuông ngân giữa quang cảnh bạt ngàn của ngôi Thiền viện này.</p>', 1, '2023-12-20 18:48:29', '2024-01-06 01:45:50'),
(8, 4, 'Làng hoa Sa Đéc', 'lang-hoa-sa-dec', 'http://127.0.0.1:8000/uploads/2023/12/lang-hoa-sa-dec.jpg', 'http://127.0.0.1:8000/uploads/2023/12/lang-hoa-sa-dec-1.jpg,http://127.0.0.1:8000/uploads/2023/12/lang-hoa-sa-dec1.jpg,http://127.0.0.1:8000/uploads/2023/12/lang-hoa-sa-dec2.jpg', 'xã Tân Qui Đông, Sa Đéc, Đồng Tháp, bên bờ sông Tiền', 'Những ngày cuối năm khi ở khắp nơi tất bật với công việc và chuẩn bị cho kỳ nghỉ Tết âm lịch thì ở một nơi cách Sài Gòn 140km cũng đang hối hả chờ ngày hoa nở, đó chính là làng hoa Sa Đéc hay còn gọi là làng hoa Tân Quy Đông.\r\n\r\nTương truyền, ngày xưa có nàng Psardek, con gái chúa đất họ Thạc, đã phải lòng một chàng trai nghèo. Phản đối mối tình này, cha nàng đã sai người trói chàng trai lại và thả trôi sông, Nàng Psardek buồn tình nên đã đi tu.\r\n\r\nVề sau, khi cha mất, nàng dùng tài sản của gia đình để làm việc từ thiện, tu bổ đường xá, xây cất chợ búa. Từ đó người ta gọi chợ và vùng này là Psardek, lâu ngày đọc thành Sa Đéc.\r\nĐến làng hoa kiểng Sa Đéc vào mùa hoa nở rộ khoảng gần tết âm lịch, bạn sẽ được chiêm ngưỡng cảnh tượng ngoạn mục với muôn loài hoa khoe sắc, nhuộm thắm cả làng hoa. Dưới đây là 1 số hình ảnh làng hoa khi vào mùa thu hoạch', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15701.324918146052!2d105.7396574487569!3d10.315354536906417!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310a7e76349d642d%3A0xb98fd65d1eb98bee!2zTMOgbmcgaG9hIFNhIMSQw6lj!5e0!3m2!1svi!2s!4v1703149191373!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Giá vé trung bình từ 20.000đ – 100.000đ/người', '<p style=\"text-align: justify;\">Làng hoa kiểng Sa Đéc cách trung tâm thành phố Sa Đéc 03km, có diện tích 313ha, với 1.968 hộ dân sinh sống. Thế mạnh của làng hoa là có hai mặt tiếp giáp với sông Tiền và sông Sa Đéc, thuận tiện cho việc vận chuyển bằng đường thủy và đường bộ. Nghề trồng hoa kiểng được hình thành từ những năm 1930, tiêu biểu là vườn hồng của ông Dương Hữu Tài (Tư Tôn) có hơn 400 loài hoa và dược liệu quý.</p>\r\n<p style=\"text-align: justify;\">Nói đến hoa và kiểng, người ta không thể không nghĩ đến địa danh Sa Đéc. Gần một trăm năm qua, Sa Đéc nổi tiếng khắp cả nước với nghề trồng hoa kiểng, cây cảnh truyền thống. Sản phẩm hoa, cây cảnh của Sa Đéc cung cấp cho các tỉnh vùng đồng bằng sông Cửu Long, thành phố Hồ Chí Minh, các tỉnh miền Trung, thị trường Campuchia và đang hướng đến các thị trường xuất khẩu khác.</p>\r\n<p style=\"text-align: justify;\">Về làng quê hiền hòa xã Tân Quy Đông, thành phố Sa Đéc, vào bất cứ tháng nào trong năm, du khách cũng có cảm giác như lạc vào thế giới đầy màu sắc và hương thơm kỳ ảo của các loài hoa và cây cảnh: mai vàng, cau kiểng, tùng, vạn niên tùng, hồng, cúc, sứ các loại, bon sai…có mặt khắp nơi đua nhau khoe hương, khoe sắc… nhưng nhiều nhất về số lượng, chủng loại ở đây là hoa hồng. Làng hoa hiện nay còn lưu giữ được trên 50 giống hoa hồng. Không những vậy, làng hoa Sa Đéc còn là xứ sở của nhiều loại cây kiểng quý hiếm tuổi thọ hàng trăm năm. Mỗi thế cây, dáng đứng đều thấm đượm văn hóa và triết học phương Đông. Có những loại cây rất bình dị gần gũi với đời sống hàng ngày như: khế, cau, bùm sụm, si, mai…qua bàn tay khéo léo tài hoa của các nghẹ nhân đã trở thành những cây kiểng quý, có hình dáng đẹp, lạ…</p>\r\n<p style=\"text-align: justify;\">Đến làng hoa kiểng Sa Đéc, du khách được đắm mình trong thế giới của muôn hoa với vô vàng hương thơm quyến rũ, bao nhiêu mệt mõi tan biến chỉ còn lại cảm giác thanh thản yên bình</p>', 1, '2023-12-20 18:59:56', '2024-01-04 01:40:54'),
(9, 3, 'Cánh Đồng Điện Gió Bạc Liêu', 'canh-dong-dien-gio-bac-lieu', 'http://127.0.0.1:8000/uploads/2023/12/canh-dong-bac-lieu.jpg', 'http://127.0.0.1:8000/uploads/2023/12/canh-dong-bac-lieu-1.jpg,http://127.0.0.1:8000/uploads/2023/12/canh-dong-bac-lieu1.jpg,http://127.0.0.1:8000/uploads/2023/12/canh-dong-bac-lieu2.jpg', 'Biển Đông A, xã Vĩnh Trạch Đông, cách trung tâm thành phố Bạc Liêu', 'Cánh đồng quạt gió Bạc Liêu là cánh đồng điện gió đầu tiên của Đông Nam Á được xây dựng trên thềm lục địa. Ban đầu khu vực này là khu vực nước lợ. Người dân thường ngăn khu vực này thành điểm nuôi tôm.\r\nNăm 2010, nơi đây được biến thành khu điện gió để khai thác tiềm năng gió, hòa vào điện lưới quốc gia. \r\nNổi bật trên nền trời xanh ngắt là những chiếc chong chóng quay (tuabin gió) màu trắng. Tất cả tạo nên những khung hình tuyệt đẹp. Đây là địa điểm sống ảo hoàn hảo cho những ai thích chụp ảnh.\r\nRiêng thời điểm lý tưởng nhất để chiêm ngưỡng vẻ đẹp của toàn cảnh nơi đây là khi bình minh hoặc lúc hoàng hôn. Trên nền trời ráng vàng ráng đỏ, những chiếc tuabin chong chóng quay đều tạo nên một khung cảnh đẹp như trong mơ', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15752.062250936491!2d105.81175094853425!3d9.2429058532339!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a1a121ca1abaa9%3A0xb6fde3efd152c47e!2zQ8OhbmggxJDhu5NuZyDEkGnhu4duIEdpw7MgQuG6oWMgTGnDqnU!5e0!3m2!1svi!2s!4v1703150082794!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Miễn phí', '<p style=\"text-align: justify;\">Cánh đồng điện gió Bạc Liêu không chỉ là địa điểm nổi tiếng khi đến du lịch Bạc Liêu mà còn là dự án phát triển năng lượng quan trọng, đặc biệt của tỉnh Bạc Liêu và toàn khu vực miền Tây. Nhà máy điện sản xuất khoảng 320 triệu kilowatt điện hàng năm. Dù đây chỉ là một phần nhỏ trong tổng lượng điện tiêu thụ của Việt Nam nhưng đây vẫn xứng đáng là một mô hình tốt có thể nhân rộng ra nhiều nơi trên cả nước.</p>\r\n<p style=\"text-align: justify;\">Thời điểm đẹp nhất để đến với cánh đồng điện gió Bạc Liêu là từ 6h đến 9h hoặc sau 16h. Theo kinh nghiệm du lịch Bạc Liêu nếu muốn ngắm cảnh đẹp nhất thì bạn nên đến đây vào sáng sớm hoặc chiều muộn. Không khí cũng thoải mái và mát mẻ. Nắng nóng, nhiệt độ tăng cao sẽ ảnh hưởng lớn đến các hoạt động vui chơi giải trí, du lịch nên bạn không nên đi vào buổi trưa.</p>\r\n<p style=\"text-align: justify;\">Địa điểm độc đáo, kích thích sự tò mò của du khách - điểm du lịch cánh đồng điện gió đã góp phần tạo nên những dấu ấn có một không hai cho Bạc Liêu, giúp nơi đây sở hữu cho mình sản phẩm du lịch khác với các địa phương trong vùng Đồng bằng sông Cửu Long trong những năm gần đây.</p>\r\n<p style=\"text-align: justify;\">Khách du lịch khi đặt chân đến cánh đồng gió Bạc Liêu sẽ ngỡ ngàng trước thiên nhiên vô cùng tuyệt vời, trời xanh trong vắt và không khí vô cùng dễ chịu. Ở trung tâm là chiếc cối xay gió lớn mà chỉ có thể thấy ở trời Tây. Những khung hình tuyệt đẹp này được các bạn trẻ chụp lại để tạo nên một không gian đậm chất Hà Lan - nơi có nhiều cối xay gió.</p>\r\n<p style=\"text-align: justify;\">Cánh đồng gió Bạc Liêu chỉ cách mẹ Nam Hải năm cây số. Bạn cũng có thể ghé thăm tượng Phật Bà Quan Âm được chạm khắc tinh xảo và ghé thăm một trong những địa điểm linh thiêng nhất ở miền Tây. Đặc biệt nhất, có lẽ du khách ai cũng sẽ từng nghe qua cái tên Công Tử Bạc Liêu, nơi đây sẽ cách địa điểm Cánh đồng gió 20 km. Là một trong những điểm tham quan thu hút nhiều du khách nhất đến với Bạc Liêu. Địa danh này gắn liền với truyền thuyết đốt tiền để luộc trứng trong một bài hát.</p>\r\n<p style=\"text-align: justify;\">Cánh đồng gió Bạc Liêu chỉ cách mẹ Nam Hải năm cây số. Bạn cũng có thể ghé thăm tượng Phật Bà Quan Âm được chạm khắc tinh xảo và ghé thăm một trong những địa điểm linh thiêng nhất ở miền Tây. Đặc biệt nhất, có lẽ du khách ai cũng sẽ từng nghe qua cái tên Công Tử Bạc Liêu, nơi đây sẽ cách địa điểm Cánh đồng gió 20 km. Là một trong những điểm tham quan thu hút nhiều du khách nhất đến với Bạc Liêu. Địa danh này gắn liền với truyền thuyết đốt tiền để luộc trứng trong một bài hát.</p>\r\n<p style=\"text-align: justify;\"> </p>', 1, '2023-12-20 19:14:50', '2024-01-06 01:46:15'),
(10, 2, 'Di tích lịch sử quốc gia Nọc Nạng', 'noc-nang', 'http://127.0.0.1:8000/uploads/2023/12/noc-nang.jpg', 'http://127.0.0.1:8000/uploads/2023/12/noc-nang-1.jpg,http://127.0.0.1:8000/uploads/2023/12/noc-nang1.jpg,http://127.0.0.1:8000/uploads/2023/12/nong-nan.jpg', 'xã Phong Thạnh, huyện Giá Rai, tỉnh Bạc Liêu', 'Ở nơi diễn ra cuộc nổi dậy của anh em Mười Chức trên cánh đồng Nọc Nạng (xã Phong Thạnh, huyện Giá Rai, tỉnh Bạc Liêu) cách đây gần một thế kỷ hiện đã trở thành Di tích lịch sử - văn hóa cấp quốc gia, góp phần giúp thế hệ trẻ hiểu hơn về công cuộc đấu tranh của nông dân Nam Bộ, chống lại chế độ cai trị hà khắc của thực dân và phong kiến.\r\nXưa kia Giá Rai là vùng đất hoang vu toàn là sình lầy với rừng tràm, lau sậy và đầy cỏ dại. Khi những lưu dân đầu tiên đến đây khai khẩn đã phải chặt cây làm nọc đóng xuống sình rồi gác nạng lên để làm nhà nhằm tránh thú dữ và rắn độc và cái tên Nọc Nạng đã ra đời như thế.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15752.175261006743!2d105.43010904853378!3d9.240379553272614!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a1150f97a6b54b%3A0x7bceae79d173b6a0!2zRGkgdMOtY2ggTOG7i2NoIHPhu60gTuG7jWMgTuG6oW5n!5e0!3m2!1svi!2s!4v1703150538752!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Miễn phí', '<p style=\"text-align: justify;\">Cũng từ địa danh này, lịch sử đã ghi nhận một sự kiện vào năm 1928 vang động cả Đông Dương, đó là cuộc nổi dậy của gia đình anh em nông dân Mười Chức chống chính quyền thực dân và bè lũ tay sai.<br /><br />Vào đầu những năm 1900 của thế kỷ trước, Hương chánh Nguyễn Thành Luông được cha mình để lại 73ha đất khai phá thuộc khu rừng ở rạch Nọc Nạng. Năm 1912, Hương chánh Luông làm đơn xin đo đạc - cấp bằng khoán cho toàn bộ diện tích 73ha và đã được chủ tỉnh Bạc Liêu chấp thuận, có cả tờ bản đồ phần đất. Sau đó, con trai ông là Nguyễn Văn Toại (còn gọi là Biện Toại) tiếp tục thừa kế phần đất khi ông qua đời.</p>\r\n<p style=\"text-align: justify;\">Khu di tích Nọc Nạng bao gồm nhiều hạng mục khác nhau như: Nhà lưu niệm - nơi trưng bày các hiện vật trong cuộc đấu tranh năm xưa; Mô hình cánh đồng - tái hiện lại cảnh sinh hoạt của nông dân Bạc Liêu cách đây một thế kỷ; Mô hình đấu tranh của nông dân Bạc Liêu chống bọn địa chủ và thực dân - tái hiện lại sự kiện Nọc Nạng năm 1928. Và phần còn lại là khu mộ của gia đình ông Mười Chức.</p>\r\n<p style=\"text-align: justify;\">Khu di tích Nọc Nạng bao gồm nhiều hạng mục khác nhau như: Nhà lưu niệm - nơi trưng bày các hiện vật trong cuộc đấu tranh năm xưa; Mô hình cánh đồng - tái hiện lại cảnh sinh hoạt của nông dân Bạc Liêu cách đây một thế kỷ; Mô hình đấu tranh của nông dân Bạc Liêu chống bọn địa chủ và thực dân - tái hiện lại sự kiện Nọc Nạng năm 1928. Và phần còn lại là khu mộ của gia đình ông Mười Chức.</p>', 1, '2023-12-20 19:22:48', '2024-01-04 01:42:24'),
(11, 2, 'Nhà thờ Cái Bè', 'nha-tho-cai-be', 'http://127.0.0.1:8000/uploads/2023/12/nha-tho-cai-be.jpg', 'http://127.0.0.1:8000/uploads/2023/12/nha-tho-cai-be-1.jpg,http://127.0.0.1:8000/uploads/2023/12/nha-tho-cai-be1.jpg', 'Thị trấn Cái Bè, huyện Cái Bè, tỉnh Tiền Giang', 'Nhà thờ Cái Bè thuộc thị trấn Cái Bè, huyện Cái Bè, tỉnh Tiền Giang là nhà thờ với kiến trúc đẹp, có tháp chuông cao nhất và có địa thế đẹp nhất trong tất cả các nhà thờ ở đồng bằng sông Cửu Long.\r\nTọa lạc bên ngã ba sông Cái Bè – nơi tụ họp của chợ nổi Cái Bè nổi tiếng, nhà thờ Cái Bè nổi bật giữa một vùng sông nước tấp nập thuyền bè qua lại.\r\nNhà thờ do linh mục Adophe Keller người Đức và bà con giáo xứ Cái Bè xây dựng từ năm 1929-1932. Nhà thờ Cái Bè có lối kiến trúc Roman của phương tây bằng bê tông cốt thép đúc đá, qua bao thăng trầm thời gian vẫn giữ được dáng vẻ đẹp thanh thoát, cổ kính.\r\n\r\nMặt bằng nhà thờ có hình Thánh giá với hai cánh ngang rất cân đối, gồm một lòng chính và hai lòng phụ với khuôn viên rộng và mát mẻ. Nhìn từ trên cao, nhà thờ như một dấu chữ thập khổng lồ nổi bật giữa khuôn viên cây xanh và xóm làng bình dị.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15700.198243254614!2d106.02497164876188!3d10.337919036565264!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310a9bb6daaf3575%3A0x1f2dfc388dfaf356!2zTmjDoCBUaOG7nSBDw6FpIELDqA!5e0!3m2!1svi!2s!4v1703151434323!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Miễn phí', '<p style=\"text-align: justify;\">Tháp chuông có bộ chuông rất lớn gồm 4 trái, được đúc tại Pháp vào năm 1931, với kỹ thuật thiết kế quả lắc chuông và thanh treo chuông rất tiên tiến.</p>\r\n<p style=\"text-align: justify;\">Bộ chuông nhà thờ Đức Bà Sai Gon do một hãng khác đúc thì thua xa về kỹ thuật thiết kế quả lắc của của nhà thờ này. Dưới chân tháp chuông là một hầm chứa nước khá lớn nhằm khuếch đại âm thanh của tiếng chuông.  Tiếng chuông nhà thờ là âm thanh của sự yên lành, thanh bình, thánh thiện, gieo vào lòng người những cung bậc của bác ái và hân hoan.Mặt ngoài nhà thờ mang những nét trang trí và hoa văn đắp nổi rất cầu kỳ và ấn tượng.Ở hai cổng bên của mặt chính diện nhà thờ khắc hoa văn hình giàn nho.</p>\r\n<p style=\"text-align: justify;\">Mái vòm cao, chia múi với những hoa văn đơn giản mà tinh tế. Nội thất tráng lệ cùng những bức tranh được bài trí trang trọng bên trong nhà thờ. Tấm tranh bằng kính màu vừa có tác dụng chiếu sáng nội thất thánh đường vừa mang tính thẩm mỹ độc đáo, tạo nên một không gian linh thiêng cho những tín đồ tin tưởng nguyện cầu.</p>\r\n<p style=\"text-align: justify;\">Nhà thờ có 5 bàn thờ bằng đá cẩm thạch quý và một bộ cửa kính màu rất đẹp. Khi bước vào bên trong nhà thờ du khách sẽ cảm nhận được một thiết kế giàu tính thẩm mỹ, hàng ghế ngồi được đặt ngay ngắn hai bên lối đi.</p>\r\n<p style=\"text-align: justify;\">Trong ánh sáng hừng đông, toàn bộ quần thể nhà thờ trở nên đẹp lộng lẫy với tháp chuông cao vút, tượng đức mẹ uy nghi trên nền trời xanh, mái nâu sáng rực, dòng sông hiền hòa cũng bừng lên phản chiếu ánh bình minh.</p>\r\n<p style=\"text-align: justify;\">Ngoài việc mang giá trị về tôn giáo nhà thờ Cái Bè cũng được xem như một công trình kiến trúc độc đáo, là nơi nhiều khách <a class=\"auto-link\" title=\"du lịch Tiền Giang\" href=\"https://thamhiemmekong.com/thong-tin-du-lich-mien-tay/dia-diem-du-lich-tien-giang-sieu-dep-nhat-dinh-phai-den.html\" target=\"_top\">du lịch Tiền Giang</a> đến tham quan chụp hình. Trong chuyến đi bạn nên kết hợp với các điểm đến như <a class=\"auto-link\" title=\"chợ nổi Cái Bè\" href=\"https://thamhiemmekong.com/thong-tin-du-lich-mien-tay/cho-noi-cai.html\" target=\"_top\">chợ nổi Cái Bè</a>, làng cổ Đông Hòa Hiệp và vườn trái cây.</p>', 1, '2023-12-20 19:37:21', '2024-01-06 08:41:56'),
(12, 3, 'Rừng Tràm Trà Sư', 'rung-tram-tra-su', 'http://127.0.0.1:8000/uploads/2023/12/rung-tram-su.jpg', 'http://127.0.0.1:8000/uploads/2023/12/rung-tram-su-1.jpg,http://127.0.0.1:8000/uploads/2023/12/rung-tram-su1.jpg,http://127.0.0.1:8000/uploads/2023/12/rung-tram-su2.jpg,http://127.0.0.1:8000/uploads/2023/12/rung-tram-su3.jpg', 'Khu vực núi Trà Sư của huyện Tịnh Biên, An Giang.', 'Rừng tràm Trà Sư là tên gọi cánh rừng có nhiều cây tràm tọa lạc gần khu vực núi Trà Sư của huyện Tịnh Biên, An Giang. Tên gọi Trà Sư có người cho rằng nghĩa là là ông thầy tu. Trong đó “Trà” là biến âm của “tà” – trong tiếng Khmer có nghĩa là ông; còn “Sư” được hiểu theo nghĩa Hán Việt là ông thầy tu. Thậm chí có ý kiến cho rằng “Trà Sư” có ý nghĩa là một ông sư (thầy chùa) tên Trà.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3921.924813652977!2d105.0579324!3d10.585051499999999!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310a1fccc2bc43b9%3A0xf03dafe8c807a960!2zUuG7q25nIFRyw6BtIFRyw6AgU8aw!5e0!3m2!1svi!2s!4v1703151719087!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Người lớn: 100.000 đồng/người    Trẻ em từ 1-10 tuổi: 50.000 đồng/người     Trẻ em dưới 1 tuổi: Miễn phí', '<p style=\"text-align: justify;\">Rừng tràm Trà Sư là tên gọi cánh rừng có nhiều cây tràm tọa lạc gần khu vực núi Trà Sư của huyện Tịnh Biên, An Giang. Tên gọi Trà Sư có người cho rằng nghĩa là là ông thầy tu. Trong đó “Trà” là biến âm của “tà” – trong tiếng Khmer có nghĩa là ông; còn “Sư” được hiểu theo nghĩa Hán Việt là ông thầy tu. Thậm chí có ý kiến cho rằng “Trà Sư” có ý nghĩa là một ông sư (thầy chùa) tên Trà.</p>\r\n<p style=\"text-align: justify;\">Rừng Tràm Trà Sư được hình thành vào năm 1983, từ một vùng trũng hoang hóa, bị nhiễm phèn nặng đã được Lâm trường Tịnh Biên trồng tràm thử nghiệm để góp phần cải tạo đất và ngăn lũ đầu nguồn. Rừng tràm Trà Sư rộng 845ha nằm trên địa bàn 3 xã Vĩnh Trung, xã Văn Giáo của huyện Tịnh Biên và một phần giáp xã Ô Long Vỹ của huyện Châu Phú (An Giang).</p>\r\n<p style=\"text-align: justify;\">Rừng Tràm Trà Sư có tác dụng rất quan trọng đối với môi trường nước và điều hòa khi hậu cho cả vùng Bảy Núi là nơi sinh sống của nhiều loài động, thực vật quý thuộc hệ thống rừng đặc dụng Việt Nam. Kết quả nghiên cứu mới nhất của đại học An Giang, rừng tràm Trà Sư là nơi cư trú của 70 loài chim, cò, trong đó có hai loài có tên trong “Sách đỏ Việt Nam” là cò lạo Ấn Độ (Giang Sen) và cò cổ rắn (Điêng Điểng). Hệ sinh thái cũng rất phong phú với 22 loài bò sát, 11 loài thú, 23 loài thủy sản, trong đó có 2 loài có giá trị khoa học và đang đứng trước nguy cơ tuyệt chủng là cá trê trắng và cá còm. Không chỉ phong phú về động vật, rừng tràm Trà Sư còn rất đa dạng về thực vật với 140 loài thuộc 52 họ và 102 chi, trong đó có gần 80 loài dược liệu.</p>\r\n<p style=\"text-align: justify;\">Với không gian bạt ngàn màu xanh của tràm cùng với bức tranh muôn màu muôn vẻ của hệ thống thực, động vật… rừng tràm Trà Sư không chỉ là điểm đến khó thể bỏ qua đối với du khách, mà còn được được đánh giá có tầm quan trọng quốc tế trong công tác bảo tồn đất ngập nước tại Đồng bằng sông Cửu Long</p>\r\n<p style=\"text-align: justify;\">Theo kinh nghiệm <a class=\"auto-link\" title=\"du lịch An Giang\" href=\"https://thamhiemmekong.com/thong-tin-du-lich-mien-tay/kinh-nghiem-du-lich-an-giang.html\" target=\"_top\">du lịch An Giang</a> của thổ địa thì rừng tràm Trà Sư vào mùa nước nổi khoảng thời gian từ tháng 9 đến tháng 11 là thời điểm đẹp nhất. Cả khu rừng được bao chùm một màu xanh tươi, đầy sức sống và dường như tất cả những vẻ đẹp thiên nhiên đều tập trung tại nơi đây, tạo nên một bức tranh thiên nhiên vô cùng sống động.</p>\r\n<p style=\"text-align: justify;\">Trong đó, thời gian đẹp nhất để tham quan rừng tràm đó là lúc 7h đến 9h sáng bạn sẽ được tận mắt chiêm ngưỡng và ngắm nhìn rất nhiều loài chim tụ tập tới đây vui đùa, nhảy nhót, hót líu lo. Sau đó, đến 17h – 18h chiều, bạn có thể đi lên đài quan sát ngắm nhìn từng đàn chim đang bay trở về tổ.</p>', 1, '2023-12-20 19:42:04', '2024-01-04 01:53:18'),
(13, 1, 'Khu lưu niệm Giáo sư, viện sĩ Trần Đại Nghĩa', 'tran-dai-nghia', 'http://127.0.0.1:8000/uploads/2023/12/tran-dai-nghia.jpg', 'http://127.0.0.1:8000/uploads/2023/12/tran-dai-nghia-1.jpg,http://127.0.0.1:8000/uploads/2023/12/tran-dai-nghia1.jpg,http://127.0.0.1:8000/uploads/2023/12/tran-dai-nghia2.jpg,http://127.0.0.1:8000/uploads/2023/12/tran-dai-nghia3.jpg', 'Xã Tường Lộc, huyện Tam Bình, tỉnh Vĩnh Long.', 'Ngày 24/11/2013, Ủy ban nhân dân tỉnh Vĩnh Long tổ chức khởi công xây dựng Khu lưu niệm Giáo sư, Viện sĩ Trần Đại Nghĩa - Người học trò xuất sắc của Chủ tịch Hồ Chí Minh, người con ưu tú của quê hương Vĩnh Long, tại xã Tường Lộc, huyện Tam Bình. Lãnh đạo các bộ, ngành Trung ương, địa phương và đông đảo nhân dân địa phương cùng tham dự. Giáo sư, Viện sĩ Trần Đại Nghĩa tên thật là Phạm Quang Lễ, là một trong những vị tướng đầu tiên của Quân đội Nhân dân Việt Nam. Ông là nhà trí thức yêu nước, nhà khoa học lớn, có uy tín cao, đã cống hiến trọn cuộc đời cho sự nghiệp khoa học Việt Nam, đặc biệt là khoa học quân sự. Khu lưu niệm Giáo sư, Viện sĩ Trần Đại Nghĩa được thiết kế xây dựng trên diện tích khoảng 16.000 m2, theo lối không gian mở, thoáng mát, nhẹ nhàng, gần gũi, nhưng vẫn đảm bảo tính tôn nghiêm, phù hợp với truyền thống văn hóa của dân tộc Việt Nam. Khu lưu niệm bao gồm các hạng mục chính như: Nhà tưởng niệm, nhà trưng ...', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15714.385525048026!2d105.97674174869961!3d10.050126240923998!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a0701bfa4bd8f3%3A0x45445c85d7651719!2zS2h1IHTGsOG7n25nIG5p4buHbSBHUyBUcuG6p24gxJDhuqFpIE5naMSpYQ!5e0!3m2!1svi!2s!4v1703152191776!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Miễn phí', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 13pt;\">Giáo sư, Viện sĩ Trần Đại Nghĩa tên thật là Phạm Quang Lễ, là một trong những vị tướng đầu tiên của Quân đội Nhân dân Việt Nam. Ông là nhà trí thức yêu nước, nhà khoa học lớn, có uy tín cao, đã cống hiến trọn cuộc đời cho sự nghiệp khoa học Việt Nam, đặc biệt là khoa học quân sự. Khu lưu niệm Giáo sư, Viện sĩ Trần Đại Nghĩa được thiết kế xây dựng trên diện tích khoảng 16.000 m2, theo lối không gian mở, thoáng mát, nhẹ nhàng, gần gũi, nhưng vẫn đảm bảo tính tôn nghiêm, phù hợp với truyền thống văn hóa của dân tộc Việt Nam. Khu lưu niệm bao gồm các hạng mục chính như: Nhà tưởng niệm, nhà trưng bày, phòng hội thảo, chiếu phim, sinh hoạt truyền thống, quảng trường, các hạn mục phụ trợ và cảnh quan, góp phần vào việc giữ gìn, tôn vinh đậm nét thân thế, công lao, sự nghiệp của Giáo sư, Viện sĩ Trần Đại Nghĩa cũng như giáo dục truyền thống, khơi dậy niềm tự hào đối với đông đảo các tầng lớp nhân dân.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 13pt;\"> Đặc biệt, là Trung tâm tích hợp dữ liệu về khoa học và công nghệ là một công trình hiện đại, mới lạ, được Bộ Khoa học và Công nghệ đầu tư xây dựng đầu tiên nằm trong khuôn viên khu lưu niệm, gồm: Cổng thông tin điện tử giới thiệu Khu lưu niệm và tích hợp thư viện điện tử giới thiệu thân thế, sự nghiệp và các công trình nghiên cứu của GS-VS Trần Đại Nghĩa, là nơi lưu giữ và quảng bá thông tin khoa học công nghệ, phục vụ khai thác sản xuất nông nghiệp, phát triển nông thôn mới vùng Đồng bằng sông Cửu Long; đồng thời phục vụ rộng rãi khách tham quan, nghiên cứu, tiếp cận thông tin khoa học công nghệ trong nước và quốc tế.</span></p>', 1, '2023-12-20 19:50:02', '2024-01-04 01:56:53');
INSERT INTO `locations` (`id`, `categories_id`, `name`, `slug`, `thumnail`, `slides`, `address`, `description`, `maps`, `price`, `detail`, `status`, `created_at`, `updated_at`) VALUES
(14, 2, 'Chùa Dơi Sóc Trăng', 'chua-doi-soc-trang', 'http://127.0.0.1:8000/uploads/2023/12/chua-doi-st.png', 'http://127.0.0.1:8000/uploads/2023/12/chua-doi-st-1.png,http://127.0.0.1:8000/uploads/2023/12/chua-doi-st1.png,http://127.0.0.1:8000/uploads/2023/12/chua-doi-st3.png', 'Nằm bên đường Văn Ngọc Chính (có bảng chỉ dẫn) thuộc Phường 3, thành phố Sóc Trăng', 'Chùa Dơi còn gọi chùa Mã Tộc (hay chùa Mahatúp) nằm bên đường Văn Ngọc Chính (có bảng chỉ dẫn) thuộc Phường 3, thành phố Sóc Trăng. Sở dĩ có cái tên đặc biệt này là vì chùa là ngôi nhà của những bầy dơi đông đúc. Ngôi chùa là không gian văn hóa duy nhất thờ Phật Thích Ca của cộng đồng dân tộc Khmer Nam Bộ tại tỉnh Sóc Trăng.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15736.751325623336!2d105.96160014860148!3d9.579071648092738!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a04d864a63a18d%3A0xe524b0c8be590a88!2zQ2jDuWEgRMahaQ!5e0!3m2!1svi!2s!4v1703152679737!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', NULL, '<p style=\"text-align: justify;\">Theo thư tịch cổ còn lại có ghi chép: Chùa được khởi công xây dựng vào từ năm 1569, cách nay đã hơn 440 năm. Ban đầu, chính điện của chùa chỉ được xây dựng bằng tre lá, sau đó được xây lại bằng gạch, mái lợp ngói. Năm 1960, chùa được sửa chữa lớn ở chánh điện và cho đến khi có được vẻ khang trang đẹp đẽ như hiện nay, chùa đã trải qua nhiều lần trùng tu tôn tạo.</p>\r\n<p style=\"text-align: justify;\">Năm 2008, một sự việc không may xảy ra khiến chùa bị cháy khu vực chánh điện. Nhưng liên tiếp những tín hiệu đáng mừng khi vào tháng 4/ 2009, chánh điện chùa đã được phục chế lại như cũ.Năm 2013, khu du lịch <strong>Chùa Dơi tỉnh Sóc Trăng</strong> được đưa vào hoạt động. Tuy ngân sách không được dư dả nhưng khu du lịch nằm phía đối diện cổng chùa có bãi đậu xe rộng rãi, các dịch vụ tiện ích khác như nhà hàng, xe điện… rất được lòng du khách.</p>\r\n<p style=\"text-align: justify;\">Từ năm 1999, chùa Dơi đã được công nhận là Di tích lịch sử – văn hóa cấp quốc gia. Cho đến nay, chính quyền Sóc Trăng vẫn đang xem xét các chính sách bảo tồn và tôn tạo nơi đây để vừa giáo dục tín ngưỡng, vừa đưa chùa Dơi thành điểm du lịch quen thuộc của tỉnh</p>\r\n<p style=\"text-align: justify;\">Tuy là không gian thờ Phật Thích Ca nhưng <strong>kiến trúc chùa Dơi Sóc Trăng</strong> vẫn bị ảnh hưởng mạnh mẽ bởi văn hóa Khmer. Ngôi chùa nổi bật trong không gian xanh mát của cây cối nhờ sắc màu vàng cam Khmer đặc trưng.</p>\r\n<p style=\"text-align: justify;\">Chùa có mái được lợp ngói, bốn đầu mái cong vút chạm trổ hình rắn Naga, trên đỉnh mái có một ngọn tháp nhọn. Bao quanh chánh điện là các hàng cột đỡ, mỗi cột có một tượng tiên nữ Kemnar chắp hai tay trước ngực…</p>\r\n<p style=\"text-align: justify;\">Tiến sâu vào thánh điện, ta sẽ thấy pho tượng Phật Thích Ca bằng đá nguyên khối đặt trên một tòa sen cao khoảng 2m. Ấn tượng không kém ngay gần đó là một pho tượng miêu tả Đức Phật cưỡi trên rắn thần Muchalinda.</p>\r\n<p style=\"text-align: justify;\">Khách du lịch có cơ hội tìm hiểu cặn kẽ về cuộc đời đức Phật qua những bức tranh miêu tả Đức Phật, từ lúc ra đời cho tới khi được khai minh rồi nhập Niết bàn..  Các bộ kinh ghi trên lá cây thốt nốt cùng những hiện vật quý hiếm mang giá trị đặc sắc về văn hóa tín ngưỡng của vùng đất Nam Bộ cũng được lưu giữ trong khuôn viên trang nghiêm của chùa. Hướng dẫn viên du lịch chắn chắn sẽ <strong>thuyết minh về chùa Dơi Sóc Trăng</strong> giúp bạn hiểu hơn nhiều điều.</p>\r\n<p style=\"text-align: justify;\">Thăm thú chùa Dơi một vòng, du khách có thể nghỉ chân ở hàng ghế dưới bóng cây cổ thụ dịu mát. Trong khuôn viên còn có xây nhiều bảo tháp chứa di hài các sư trụ trì chùa, và nhà hội Sa La với kiến trúc kiểu nhà rông, làm nơi nghỉ ngơi, tu học của các sư sãi…</p>\r\n<p style=\"text-align: justify;\">Đến chùa Dơi thì quả thật không nên bỏ qua những câu chuyện loài dơi làm nên cái tên của chùa trong lòng người dân tứ xứ. Khuôn viên chùa có nhiều cây sao và dầu – nơi trú ẩn của hàng vạn con dơi. Cứ chiều đến hàng vạn con dơi lại kéo về sân chùa che kín cả bầu trời. Khác với tâm lí sợ sệt loài dơi của chúng ta, các vị sư ở đây cho rằng việc dơi đổ về chùa là phúc lành nhà Phật cho ngôi chùa này nên họ rất tích cực bảo vệ bầy dơi.</p>\r\n<p style=\"text-align: justify;\">Dường như loài dơi hiểu tấm lòng của người nhà Phật nên lạ lùng thay, chưa một cây trái nào trong vườn bị chúng dòm ngó tới. Khi di chuyển, chúng cũng “biết ý” bay lượn vòng chứ không bay thẳng qua nóc ngôi chính điện của chùa.</p>\r\n<p style=\"text-align: justify;\">Đàn dơi hoạt động chủ yếu vào đêm, khoảng 6h chiều, chúng bay đi kiếm ăn và quay về vào khoảng 5h sáng hôm sau. Mặc dù ở <strong>Sóc Trăng</strong> cũng có nhiều ngôi chùa thanh tịnh khác với vườn cây bóng mát, nhưng việc bầy dơi này chỉ chọn chùa Dơi Sóc Trăng làm nơi cư trú là điều dường như vẫn còn bí ẩn. Chúng chỉ đậu trên những tán cây trong khuôn viên chùa, tuyệt nhiên không đậu ở bên ngoài.</p>\r\n<p style=\"text-align: justify;\">Cho đến nay, chưa có bất kì một giải thích hay giả thuyết nào đưa ra để giải đáp hiện tượng này. Chùa Dơi với những bí ẩn như thế được truyền tai nhau, khiến người ta không khỏi hào hứng đến thăm ngôi chùa này để tận mắt chứng kiến thực hư.</p>', 1, '2023-12-20 19:58:06', '2024-01-06 03:07:20'),
(15, 3, 'Khu Du Lịch Lâm Viên Núi Cấm', 'nui-cam', 'http://127.0.0.1:8000/uploads/2023/12/nui-cam1.jpg', 'http://127.0.0.1:8000/uploads/2023/12/nui-cam1-1.jpg,http://127.0.0.1:8000/uploads/2023/12/nui-cam2.jpg,http://127.0.0.1:8000/uploads/2023/12/nui-cam3.jpg,http://127.0.0.1:8000/uploads/2023/12/nui-cam5.jpg', 'tại xã An Hảo, huyện Tịnh Biên, tỉnh An Giang', 'Mảnh đất An Giang nổi tiếng bởi cảnh quan thiên nhiên nhuốm màu sắc huyền bí, trong đó phải kể đến vùng Bảy Núi hay Thất  Sơn. Nổi bật trong dãy Thất Sơn là ngọn núi Cấm cao khoảng 710m so với mực nước biển trung bình. Núi Cấm còn gọi là Thiên Cấm Sơn, nằm ngay trung tâm của vùng Bảy núi chính vì thế núi Cấm có vẻ đẹp hoang sơ, hùng vĩ. Đặc biệt, đỉnh Bồ Hong ở trên núi Cấm chính là đỉnh núi cao nhất trong Thất Sơn và cũng là ngọn núi cao nhất Đồng Bằng Sông Cửu Long. Với khí hậu mát mẻ được ví như một phiên bản “Đà Lạt” của miền Tây, Núi Cấm trở thành điểm tham quan, vui chơi, chiêm bái hấp dẫn bậc nhất trong các điểm du lịch An Giang.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15692.270974363597!2d105.01494920689653!3d10.495326034878408!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310a1d77158d77a1%3A0xcde7c542221567c9!2zS2h1IGR1IGzhu4tjaCBMw6JtIFZpw6puIE7DumkgQ-G6pW0!5e0!3m2!1svi!2s!4v1703167864542!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Miễn phí', '<p style=\"text-align: justify;\">Núi Cấm là ngọn núi cao và hùng vĩ nhất trong cụm Thất Sơn được nhiều người mộ đạo coi là ngọn núi thiêng với nhiều tên gọi khác nhau. Từ xa xưa núi Cấm có tên là núi Gấm, Thiên Cấm Sơn, Thiên Cẩm Sơn (núi đẹp như gấm lụa). Cho tới nay vẫn còn nhiều giả thuyết về nguồn gốc núi Cấm, mỗi người có cách lý giải theo suy luận riêng của mình.</p>\r\n<p style=\"text-align: justify;\">Tương truyền ngày xưa núi Cấm vô cùng hiểm trở và có nhiều thú dữ, ít ai lui tới nên trở thành một vùng sơn lâm bí ẩn. Lợi dụng sự hoang vu tịch mịch nhiều nhà sư đã tìm đến ẩn tu, các phường lục lâm thảo khấu cũng như những đảng cướp vùng biên thùy phức tạp nầy cũng lấy núi Cấm làm căn cứ.</p>\r\n<p style=\"text-align: justify;\">Để không bị lộ, họ phao tin đồn rằng trên núi có rất nhiều thần linh, người dân không được bén mảng tới, ai vô cớ đặt chân lên sẽ bị quở phạt, về lâm bệnh mà chết. Người dân thời bấy giờ rất sợ và gọi đó là núi Cấm. Một truyền thuyết mang tính lịch sử là Nguyễn Ánh bị quân binh Tây Sơn truy nã đến đây, ông và đoàn tùy tùng chạy lên núi Cấm ẩn trú. Để yên bề bí mật, các cận thần của Nguyễn Ánh cho người đi phao tin rằng trong núi đầy yêu tinh, ác thú nhằm hạn chế sự nhòm ngó của dân lành. Hay lời cấm đoán của Phật Thầy Tây An  Đoàn Minh Huyên cấm không cho bổn đạo lên núi làm ô uế núi non nên mới có tên là núi Cấm…</p>\r\n<p style=\"text-align: justify;\">Từ một ngọn núi hoang vu “cấm kỵ”, giờ núi Cấm đã ra dáng là khu du lịch Lâm Viên.</p>\r\n<p style=\"text-align: justify;\">Các bạn có thể chinh phục núi Cấm An Giang vào bất kỳ thời điểm nào trong năm. Trong đó thời gian du lịch núi Cấm đẹp nhất và vào mùa xuân, vì cảnh đẹp khi ấy mới đầy đủ: cây cối đâm chồi nảy lộc, hoa trái xum xuê. Các hồ núi cao buổi sáng sớm sương trắng giăng đầy, buổi trưa nắng vàng dịu dàng, buổi chiều tối mây là đà bay, tiết trời về đêm se lạnh.</p>\r\n<p style=\"text-align: justify;\">Nếu bạn muốn hòa mình vào không khí sôi động của các lễ hội truyền thống của An Giang thì nên đi vào ngày 23. 24 tháng 4 (Âm Lịch) để tham dự lễ hội vía Bà Chúa Xứ và ngày 29 tháng 8 đến ngày mùng 01 tháng 9 âm lịch tham gia lễ hội đua bò bảy núi Tết Dolta của dân tộc Khmer.</p>\r\n<p style=\"text-align: justify;\">Lưu ý, khi đi du lịch tới núi Cấm vào 2 tháng này thì các bạn nên mang theo thêm áo mưa hoặc dù, bởi vì những tháng này thường rất hay có mưa. Còn nếu như bạn muốn ngắm nhìn An Giang mùa nước nổi tại trên đỉnh núi Cầm thì nên đi vào khoảng tháng 10 là thích hợp nhất.</p>\r\n<p style=\"text-align: justify;\">Sau khi đến chân núi Cấm – An Giang, bạn cần phải lựa chọn 3 cách đi lên đỉnh núi. Bạn có thể đi cáp treo, đi xe ôm hoặc đi bộ lên đỉnh núi. Đường đi bộ hay xe khá là dễ đi vì được xây mới với đường lộ rộng. Nếu đi cáp treo mang đến cảm giác bay bổng lãng mạn khi được ngắm nhìn phong cảnh núi non hùng vĩ, thì cảm giác ngồi trên ô tô lại rất phiêu lưu hồi hập bởi xe len lỏi qua những con đường quanh co, khúc khuỷu. Đường lên núi Cấm vòng vèo, một bên là núi, một bên là những thung lũng mát rượi bóng cây xanh, mây uốn gió lượn.</p>\r\n<p style=\"text-align: justify;\">Dịch vụ cáp treo lên núi Cấm dành cho ai có sức khỏe không tốt hoặc thời gian hạn hẹp để leo núi. Du khách chỉ mất khoảng 15 phút đi cáp treo là có thể lên tới đỉnh núi. Ngồi trong cáp treo, bạn có thể đưa tầm mắt nhìn rõ cả một khoảng trời An Giang từ trên cao xuống tuyệt đẹp.</p>', 1, '2023-12-21 00:11:10', '2024-01-06 01:46:33'),
(16, 3, 'Khu du lịch sinh thái Gáo Giồng Đồng Tháp', 'gao-giong', 'http://127.0.0.1:8000/uploads/2023/12/gao-giong.png', 'http://127.0.0.1:8000/uploads/2023/12/gao-giong2.png,http://127.0.0.1:8000/uploads/2023/12/gao-giong3.png,http://127.0.0.1:8000/uploads/2023/12/gao-giong1.png', 'Thuộc địa phận xã Gáo Giồng, huyện Cao Lãnh, tỉnh Đồng Tháp.', 'Khu du lịch sinh thái Gáo Giồng đang là điểm đến yêu thích của rất nhiều du khách trong và ngoài tỉnh. Với không gian xanh mát, đặc biệt là nằm trong khu vực rừng tràm hứa hẹn sẽ đem tới cho bạn những phút giây nghỉ ngơi thư giãn nhất. Và không làm mất quá nhiều thời gian của bạn, hãy cùng chúng tôi đi khám phá khu du lịch này qua bài viết sau đây.Nằm cách trung tâm Tp. Cao Lãnh khoảng 17km, khu du lịch sinh thái Gáo Giồng được bắt đầu thành lập từ tháng 3/2003 với diện tích 36ha. Đây chính là nơi sinh sống của 15 loài chim nước như trích mồng đỏ, cồng cộc, le le, vịt trời, diệc, điên điển', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d15687.53002866459!2d105.60141430691722!3d10.58835833348168!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310a5d77bb4927c9%3A0xe15debd88f961c72!2zS2h1IGR1IGzhu4tjaCBzaW5oIHRow6FpIEfDoW8gR2nhu5NuZw!5e0!3m2!1svi!2s!4v1703168381958!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', NULL, '<p style=\"text-align: justify;\">Trong đó nhiều nhất chính là loài cò trắng có số lượng lên tới hàng chục nghìn con. Chính vì điều mà khiến cho rừng tràm này được xem là một trong những vườn cò lớn nhất hiện nay ở vùng Đồng Tháp Mười.Theo như những thông tin ghi lại, khu vực rừng tràm Gáo Giồng trước năm 1975 chỉ là vùng đất hoang hoá, bị nhiễm phèn nặng. Lúc đó có số ít hộ dân vào mở lõm phần diện tích ven kênh rạch để trồng lúa. Tuy nhiên vài vụ sau phải bỏ vì năng suất rất thấp.</p>\r\n<p style=\"text-align: justify;\">Sau khi thực hiện chủ trương khai phá Đồng Tháp Mười, các cấp huyện Cao Lãnh đã cho khai phá vùng đất này. Vào thời điểm có rất ít người tin về hiệu quả mà chủ trương này mang lại. Có nhiều ý kiến cho rằng nên đào kênh, thau chua, rửa phèn và trồng lúa để nhằm giải quyết nhu cầu bức xúc về lương thực. Tuy nhiên để bảo vệ hệ sinh thái thì cuối cùng người ta cũng đã tiến hành trồng tràm.</p>\r\n<p style=\"text-align: justify;\">Đến năm 2003, theo chủ trương của huyện Cao Lãnh là phát triển khu du lịch sinh thái Rừng tràm Gáo Giồng với số tiền đầu tư lên tới 700 triệu đồng. Hiện nay, nơi đây chở thành khu du lịch sinh thái nổi tiếng của vùng đồng bằng sông Cửu Long và được nhiều người biết tới.</p>\r\n<p style=\"text-align: justify;\">Được biết đến là vườn cò lớn nhất vùng Đồng Tháp Mười, du khách đến đây sẽ được chiêm ngưỡng và khám phá cuộc sống của các loài chim. Mỗi khi mùa nước lên, những cánh đồng ở Gáo Giồng tràn ngập đàn cò tạo nên một khung cảnh đẹp vô cùng. Còn ở các lung sen thì lại có hàng nghìn con trích mồng đỏ đang kiếm ăn và phát ra những âm thanh bắt tai cùng những màn “trình diễn” đẹp mê hồn</p>\r\n<p style=\"text-align: justify;\"><span data-sheets-value=\"{\" data-sheets-userformat=\"{\">Giá vé khu du lịch Gáo Giồng hiện nay có </span>giá 10.000 VND cho người lớn và trẻ em thì miễn phí. Và trước khi đi thăm quan vườn chim, du khách sẽ được hướng dẫn viên phát mỗi người một ống nhòm và mời lên đài quan sát cao 18m. Đứng trên đài, du khách có thể thấy được gần như toàn bộ vườn chim và có cái nhìn tổng quát về khu du lịch.</p>\r\n<p style=\"text-align: justify;\">Sau đó, các bạn sẽ được đưa đến tiếp cận khu vườn chim bằng xuồng máy. Khi đến vườn chim, du khách sẽ tận mắt trông thấy rất nhiều loài chim khác nhau, đem tới một cảm giác mới lạ, khác biệt. Giống như bạn đang được hòa quyện với thiên nhiên hoang dã vậy.</p>\r\n<p style=\"text-align: justify;\">Theo như lời kể của hướng dẫn viên du lịch, khu du lịch Gáo Giồng được chia thành 4 khu lớn với trên 70km kênh phân lô và 20km đê bao khép kín. Không chỉ là nơi trú ngụ của các loài chim mà còn là nơi sinh sống của các loài cá. Đặc biệt, đây còn là vựa cá nước ngọt lớn vào bậc nhất hiện nay ở vùng Đồng Tháp Mười với sản lượng khai thác mỗi năm lên tới hơn 30 tấn. Chính vì vậy mà có rất nhiều loài chim nước đã kéo đến đây làm nơi sinh sống, quần tụ.</p>\r\n<p style=\"text-align: justify;\">Nhờ có nguồn hải sản phong phú mà du khách có thể thưởng thức những món ăn đặc sản truyền thống của vùng Đồng Tháp Mười. Ví dụ như cá lóc nướng gói với đọt sen, cá linh nấu canh chua bông điên điển, mắm kho chấm với rau dừa, lá sâu nhái chấm với mắm me, rắn nướng mọi,… Thêm vào đó là một ít rượu mật ong tràm tinh khiết thì còn gì bằng.</p>', 1, '2023-12-21 00:19:49', '2024-01-06 03:07:40'),
(17, 3, 'Khu du lịch Mũi Cà Mau', 'mui-ca-mau', 'http://127.0.0.1:8000/uploads/2023/12/dat-mui-ca-mau.jpg', 'http://127.0.0.1:8000/uploads/2023/12/dat-mui-ca-mau-1.jpg,http://127.0.0.1:8000/uploads/2023/12/dat-mui-ca-mau1.jpg,http://127.0.0.1:8000/uploads/2023/12/dat-mui-ca-mau2.jpg,http://127.0.0.1:8000/uploads/2023/12/dat-mui-ca-mau3.jpg', 'Tọa lạc tại xã Đất Mũi, huyện Ngọc Hiển cách trung tâm Thành phố Cà Mau khoảng 110 km.', 'Khu du lịch Mũi Cà Mau tọa lạc tại xã Đất Mũi, huyện Ngọc Hiển cách trung tâm Thành phố Cà Mau khoảng 110 km là vùng đất địa đầu phần cực Nam Tổ quốc. Nơi đây được bao bọc bởi biển Đông và vịnh Thái Lan là tọa độ duy nhất tại nước ta có thể đồng thời ngắm mặt trời mọc tại hướng Đông và lặn ở hướng Tây. Khu du lịch Mũi Cà Mau không chỉ là địa điểm nghỉ dưỡng, tham quan được nhiều tín đồ du lịch tìm đến, mà nó còn phục vụ cho việc nghiên cứu sinh thái với vai trò là Khu dự trữ sinh quyển thế giới Mũi Cà Mau, được UNESCO công nhận vào năm 2009.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d252472.3295688584!2d104.61531765386454!3d8.607504118339001!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31a414445d1ddd4f%3A0x302698714c589db8!2zQ-G7mXQgTeG7kWMgUXXhu5FjIEdpYSDEkOG6pXQgTcWpaQ!5e0!3m2!1svi!2s!4v1703168921864!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Người lớn: 100.000 đồng/người    Trẻ em từ 1-10 tuổi: 50.000 đồng/người', '<p style=\"text-align: justify;\">Ước tính đến thời điểm hiện tại, Khu du lịch Mũi Cà Mau có tổng diện tích khoảng 20.100 ha và khu vực trung tâm để đầu tư phát triển du lịch có diện tích khoảng 2,100 ha. </p>\r\n<p style=\"text-align: justify;\">Trong đó, công viên Văn hóa Du lịch Cà Mau là nơi tập trung các công trình tiêu biểu phục vụ việc tham quan, du lịch như Cột mốc tọa độ GPS 0001, Đền thờ Lạc Long Quân, Cột mốc đường Hồ Chí Minh km 2436, Biểu tượng cột cờ Hà Nội, Biểu tượng con tàu, Điểm chóp Mũi Cà Mau… Ngoài ra còn có các khu du lịch sinh thái về rừng biển, làng rừng, làng nghề sản xuất và Khu du lịch Khai Long.</p>\r\n<p style=\"text-align: justify;\"><a href=\"https://mia.vn/cam-nang-du-lich/kham-pha-cong-vien-van-hoa-du-lich-ca-mau-co-gi-hap-dan-9625\" target=\"_blank\" rel=\"noopener noreferrer\">Công viên Văn hóa du lịch Cà Mau</a> là nơi tập trung các công trình tiêu biểu phục vụ cho các hoạt động du lịch tại địa phương. Đến đây, bạn không thể nào bỏ qua việc check-in tại cột mốc tọa độ Quốc gia GPS 0001, km 2436 điểm cuối cùng của con đường Hồ Chí Minh bắt đầu từ Pác Bó Cao Bằng hay ngắm bình minh và hoàng hôn trên vùng biển cực Nam Tổ quốc. Ngoài ra, trong Khu du lịch Mũi Cà Mau còn có Đền thờ Lạc Long Quân và Tượng Mẹ Âu Cơ được đặt hướng về biển Đông, là công trình mang ý nghĩa thể hiện đạo lý “Uống nước nhớ nguồn” của nhân dân ta, nhằm nhắc nhở thế hệ mai sau về quyết tâm bảo vệ chủ quyền dân tộc và tri ân tổ tiên đã “lên rừng, xuống biển” mở mang bờ cõi. </p>\r\n<p style=\"text-align: justify;\">Khi đến với Khu du lịch Mũi Cà Mau, bạn cũng có thể tham quan biểu tượng cột cờ Hà Nội, đây là công trình mang ý nghĩa lịch sử, giáo dục to lớn do Đảng bộ, chính quyền và nhân dân Thủ đô Hà Nội trao tặng cho địa phương. Bên cạnh đó, ở Khu du lịch Mũi Cà Mau bạn còn có thể thưởng thức nhiều món ăn đặc sản được chế biến từ tôm, cua, cá, mực vô cùng hấp dẫn.</p>\r\n<p style=\"text-align: justify;\">Khi đến Khu du lịch Mũi Cà Mau, bên cạnh thăm thú những công trình kiến trúc tại Công viên Văn hóa, bạn còn được trải nghiệm khám phá rừng ngập mặn thuộc <a href=\"https://mia.vn/cam-nang-du-lich/vuon-quoc-gia-mui-ca-mau-man-nhan-voi-he-sinh-thai-phong-phu-9658\" target=\"_blank\" rel=\"noopener noreferrer\">Vườn quốc gia Mũi Cà Mau</a>. Tuy tuyến tham quan xuyên rừng Vườn quốc gia Mũi Cà Mau chỉ được đưa vào hoạt động từ năm 2018 nhưng đã thu hút rất đông tín đồ du lịch tìm đến trải nghiệm, khám phá. </p>\r\n<p style=\"text-align: justify;\">Bên cạnh việc ngồi ca nô hoặc vỏ lãi đi xuyên qua những cánh rừng ngập mặn để ngắm cảnh, bạn còn được ghé thăm mô hình nuôi hàu lồng của người dân Đất Mũi, tham quan khu vực bãi bồi, chụp ảnh cùng những gốc đước nhiều năm tuổi và trải nghiệm đi qua những chiếc cầu khỉ mang đậm chất miền Tây v.v… Ngoài ra, trong chuyến tham quan Vườn quốc gia Mũi Cà Mau bạn còn được thưởng thức những câu vọng cổ mang đậm chất giọng Cà Mau từ những hướng dẫn viên.</p>\r\n<p style=\"text-align: justify;\">Hiện tại Vườn quốc gia Mũi Cà Mau đã khai thác bốn tuyến du lịch sinh thái khác nhau, bao gồm: Tham quan rừng ngập mặn Bãi Bồi; Tham quan khám phá Giếng Trời – Rừng nguyên sinh; Tham quan diễn thế rừng tự nhiên – Cồn Ông Trang và Tham quan Bãi Bồi ven hai bờ biển đông, tây – Rừng ngập mặn.</p>', 1, '2023-12-21 00:28:47', '2024-01-04 02:06:05'),
(20, 4, 'Làng gốm Vĩnh Long', 'lang-gom-vinh-long', 'http://127.0.0.1:8000/uploads/2024/01/lang-gom-2.jpg', 'http://127.0.0.1:8000/uploads/2024/01/lang-gom-3.jpg,http://127.0.0.1:8000/uploads/2024/01/lang-gom1-1.jpg,http://127.0.0.1:8000/uploads/2024/01/lang-gom2-1.jpg', 'Mỹ Phước, Mang Thít, Vĩnh Long, Việt Nam', 'Làng gốm Vĩnh Long nằm dọc bờ sông Cổ Chiên, với hàng nghìn lò gạch, lò gốm chen chúc nhau như nấm sau mưa, nối dài hàng chục km tạo thành một bức tranh kỳ thú, trông xa tựa những lâu đài rực đỏ dưới ánh mặt trời. Bởi thế mà dân gian xứ này còn phong cho Làng gốm Vĩnh Long là \"vương quốc đỏ\".\r\nDòng Cửu Long đỏ nặng phù sa, đổ về hạ lưu bằng hai nhánh sông Tiền, sông Hậu, hàng năm mang đến cho đồng bằng Nam Bộ hàng triệu mét khối phù sa. Không chỉ bồi đắp cho những đồng lúa bạt ngàn, những miệt vườn bốn mùa hoa trái, những dòng phù sa tụ lại Vĩnh Long còn góp phần hình thành cho nơi đây những mỏ đất sét quý giá. Và người dân nơi đây đã biến chúng thành những làng gạch, làng gốm nổi danh.', '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3926.426774782685!2d106.06637531061284!3d10.22712208984806!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x310a9e08d88b5435%3A0xeacb4b38f3e27a95!2zTMOgbmcgR-G7kW0gVsSpbmggTG9uZw!5e0!3m2!1svi!2s!4v1704470299379!5m2!1svi!2s\" width=\"600\" height=\"450\" style=\"border:0;\" allowfullscreen=\"\" loading=\"lazy\" referrerpolicy=\"no-referrer-when-downgrade\"></iframe>', 'Miễn phí', '<p style=\"text-align: justify;\"><span style=\"font-size: 13pt; font-family: \'times new roman\', times, serif;\">Nghề làm gạch, ngói có mặt ở Vĩnh Long từ rất sớm, trải dài gần 30 km thuộc huyện Long Hồ và Mang Thít. Lúc bấy giờ nghề còn thịnh, mỗi nhà có đến hai ba miệng lò. Đến mùa nung các cột khói trắng ngút trời, tạo ra vô vàn sản phẩm tỏa đi khắp nơi, mang đến cuộc sống no đủ cho người dân nơi đây.</span><br /><span style=\"font-size: 13pt; font-family: \'times new roman\', times, serif;\">Thời này, Vĩnh Long tự hào về một tài nguyên đất sét đặt biệt mà không vùng nào có, hình thành nên làng nghề gạch lớn nhất vùng đồng bằng sông Cửu Long. Người dân nơi đây sinh ra đã gắn bó với nghề, qua bao thế hệ họ đã đúc kết thành kỹ thuật nung điêu luyện chỉ bằng lò gạch, nhiên liệu là trấu, và một số sản vật quen thuộc với vùng lúa nước.</span><br /><span style=\"font-size: 13pt; font-family: \'times new roman\', times, serif;\">Tuy nhiên, qua thời gian, khi các kỹ thuật và hệ thống lò công nghệ cao được đầu tư thì các lò gạch truyền thống ở Vĩnh Long dần đi vào quên lãng. Mặc dù dọc kênh Thầy Cai ở Mang Thít hiện vẫn còn hàng trăm lò gạch nằm san sát nhau nhưng hầu như đã rêu phong, bám bụi. Nhìn từ trên cao những lò nung gạch đỏ cao vút ngày nào, nay phủ màu thời gian trông như một ngôi làng cổ tích.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 13pt; font-family: \'times new roman\', times, serif;\">Tuy hình thành muộn, chỉ ngoài 20 năm, nhưng Làng gốm Vĩnh Long đã tiếp bước thành công làng gạch xưa kia, để tạo nên danh tiếng nhờ những đặc trưng riêng, đó là dòng gốm đỏ không men. Gốm Vĩnh Long có màu đỏ tự nhiên, sau khi nung ửng còn có lớp phấn trắng phơn phớt bên ngoài độc đáo. Gốm chắc, bền, màu sắc bắt mắt, dần được thị trường nội địa ưa chuộng, và thu hút sự quan tâm của cả thị trường quốc tế, mở đường cho xuất khẩu.</span><br /><br /><span style=\"font-size: 13pt; font-family: \'times new roman\', times, serif;\">Ngày nay, Làng gốm Vĩnh Long đang trở nên sôi động với hàng chục ngàn lao động, và trở thành một trong những dòng sản phẩm mũi nhọn của tỉnh. Du khách đến thăm làng gốm, xa xa đã thấy những làn khói bốc lên. Dọc tỉnh lộ, xe tải đủ loại nằm chờ hàng, công nhân thì tất bật khuân vác. Trong xưởng, thợ in, xu, chà cần mẫn làm việc. Vài nghệ nhân ngồi đăm chiêu “vọc đất”, tìm ý tưởng cho mẫu gốm mới: một nét phá cách, một tác phẩm trừu tượng, hay một thôn nữ miệt sông nước trong trẻo và thánh thiện...</span></p>', 1, '2024-01-05 09:01:49', '2024-01-06 01:43:48');

